// FACILITATOR DASHBOARD - Full Screen Projector View
// Designed for TV/projector mirroring with unified interface
import React, { useState, useEffect } from 'react';
import { Card, Button, Badge } from './components';
import { updateGamePhase, subscribeToRoom } from './firebase';

// Big Timer Component with Progress Bar
const BigTimer = ({ seconds, totalSeconds, phase }) => {
  const isUrgent = seconds < 30;
  const minutes = Math.floor(seconds / 60);
  const secs = seconds % 60;
  const progress = totalSeconds > 0 ? ((totalSeconds - seconds) / totalSeconds) * 100 : 0;

  return (
    <div className={`rounded-3xl p-6 ${isUrgent ? 'bg-red-600 animate-pulse' : 'bg-gradient-to-r from-[#3d5a4c] to-[#48a89a]'}`}>
      <div className="text-white text-center">
        <div className="text-xs uppercase tracking-wider mb-1 opacity-75">‚è±Ô∏è TIME REMAINING</div>
        <div className="text-7xl font-mono font-black">
          {minutes}:{secs.toString().padStart(2, '0')}
        </div>
        {isUrgent && (
          <div className="text-xl font-bold mt-2 animate-bounce">‚ö†Ô∏è HURRY UP!</div>
        )}

        {/* Progress bar */}
        <div className="mt-4 h-3 bg-white/20 rounded-full overflow-hidden">
          <div 
            className={`h-full transition-all duration-1000 ${isUrgent ? 'bg-white' : 'bg-[#d4a84b]'}`}
            style={{ width: `${progress}%` }}
          />
        </div>

        <div className="mt-2 text-sm opacity-75">
          Current Phase: {phase?.toUpperCase().replace(/-/g, ' ')}
        </div>
      </div>
    </div>
  );
};

// Stats Card Component
const StatsCard = ({ icon, label, value, color = 'bg-[#3d5a4c]' }) => (
  <div className={`${color} rounded-2xl p-4 text-white text-center`}>
    <div className="text-3xl mb-1">{icon}</div>
    <div className="text-4xl font-black">{value}</div>
    <div className="text-sm opacity-75">{label}</div>
  </div>
);

// Activity Feed Component
const ActivityFeed = ({ activities = [] }) => (
  <div className="bg-white rounded-2xl p-4 h-64 overflow-y-auto">
    <h3 className="text-lg font-bold text-[#3d5a4c] mb-3 sticky top-0 bg-white">
      üìã LIVE ACTIVITY
    </h3>
    {activities.length === 0 ? (
      <p className="text-gray-400 text-center py-8">Waiting for activity...</p>
    ) : (
      <div className="space-y-2">
        {activities.slice(-15).reverse().map((activity, i) => (
          <div 
            key={i} 
            className={`p-3 rounded-lg text-sm ${i === 0 ? 'bg-[#d4a84b]/20 animate-pulse' : 'bg-[#f5f3ef]'}`}
          >
            <span className="mr-2">{activity.icon}</span>
            <span className="font-medium">{activity.player}</span>
            <span className="text-gray-500 ml-1">{activity.action}</span>
            <span className="text-xs text-gray-400 ml-2">{activity.time}</span>
          </div>
        ))}
      </div>
    )}
  </div>
);

// Leaderboard Component
const Leaderboard = ({ players = [] }) => {
  const sortedPlayers = [...players].sort((a, b) => (b.score || 0) - (a.score || 0));

  return (
    <div className="bg-white rounded-2xl p-4">
      <h3 className="text-lg font-bold text-[#3d5a4c] mb-3">üèÜ LEADERBOARD</h3>
      <div className="space-y-2">
        {sortedPlayers.length === 0 ? (
          <p className="text-gray-400 text-center py-4">No players yet</p>
        ) : (
          sortedPlayers.slice(0, 10).map((player, i) => (
            <div 
              key={player.id}
              className={`flex justify-between items-center p-3 rounded-xl ${
                i === 0 ? 'bg-gradient-to-r from-[#d4a84b] to-[#c49a3a] text-white' :
                i === 1 ? 'bg-gray-200' :
                i === 2 ? 'bg-orange-100' :
                'bg-[#f5f3ef]'
              }`}
            >
              <div className="flex items-center gap-3">
                <span className="text-2xl">
                  {i === 0 ? 'ü•á' : i === 1 ? 'ü•à' : i === 2 ? 'ü•â' : `${i + 1}.`}
                </span>
                <span className="font-bold">{player.name}</span>
              </div>
              <span className="text-xl font-black">{player.score || 0}</span>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Player Grid Component
const PlayerGrid = ({ players = [] }) => (
  <div className="bg-white rounded-2xl p-4">
    <h3 className="text-lg font-bold text-[#3d5a4c] mb-3">üë• PLAYERS ({players.length})</h3>
    <div className="grid grid-cols-4 gap-2">
      {players.map(player => (
        <div 
          key={player.id}
          className={`p-2 rounded-lg text-center text-sm ${
            player.submitted ? 'bg-green-100 border-2 border-green-400' :
            player.connected ? 'bg-[#f5f3ef]' :
            'bg-red-50 opacity-50'
          }`}
        >
          <div className="font-medium truncate">{player.name}</div>
          <div className="text-xs text-gray-500">
            {player.submitted ? '‚úÖ Done' : player.connected ? 'üü¢ Active' : 'üî¥ Away'}
          </div>
        </div>
      ))}
    </div>
  </div>
);

// Phase Control Panel
const PhaseControls = ({ gameCode, currentPhase, gameType, onPhaseChange }) => {
  const phases = {
    spotTheFake: ['lobby', 'intro', 'round1', 'round1-debrief', 'round2', 'round2-debrief', 'ethics', 'legal', 'quiz', 'werewolf', 'werewolf-vote', 'results'],
    memeMachine: ['lobby', 'intro', 'issue', 'describe', 'wizard', 'generate', 'caption', 'preview', 'critique', 'finalize', 'gallery', 'vote', 'results'],
    vibeCode: ['lobby', 'intro', 'ideate', 'describe', 'generate', 'iterate', 'present', 'vote', 'results'],
  };

  const currentPhases = phases[gameType] || phases.spotTheFake;
  const currentIndex = currentPhases.indexOf(currentPhase);

  return (
    <div className="bg-gradient-to-r from-[#3d5a4c] to-[#48a89a] rounded-2xl p-4">
      <h3 className="text-white font-bold mb-3">üéÆ PHASE CONTROLS</h3>

      {/* Progress bar */}
      <div className="flex gap-1 mb-4">
        {currentPhases.map((phase, i) => (
          <div 
            key={phase}
            className={`flex-1 h-2 rounded-full transition-all ${
              i < currentIndex ? 'bg-[#d4a84b]' :
              i === currentIndex ? 'bg-white animate-pulse' :
              'bg-white/30'
            }`}
          />
        ))}
      </div>

      {/* Navigation buttons */}
      <div className="flex gap-2">
        <Button 
          variant="secondary"
          size="sm"
          onClick={() => {
            const prevPhase = currentPhases[Math.max(0, currentIndex - 1)];
            updateGamePhase(gameCode, prevPhase);
          }}
          disabled={currentIndex <= 0}
          className="flex-1"
        >
          ‚Üê PREV
        </Button>

        <div className="flex-2 text-center text-white font-bold py-2 px-4 bg-white/20 rounded-lg">
          {currentPhase?.toUpperCase().replace(/-/g, ' ')}
        </div>

        <Button 
          size="sm"
          onClick={() => {
            const nextPhase = currentPhases[Math.min(currentPhases.length - 1, currentIndex + 1)];
            updateGamePhase(gameCode, nextPhase);
          }}
          disabled={currentIndex >= currentPhases.length - 1}
          className="flex-1 bg-[#d4a84b] hover:bg-[#c49a3a]"
        >
          NEXT ‚Üí
        </Button>
      </div>
    </div>
  );
};

// Submission Gallery Component
const SubmissionGallery = ({ submissions = [], reactions = {}, gameType }) => {
  const memes = submissions.filter(s => s.type === 'meme');
  const codes = submissions.filter(s => s.type === 'code');
  const answers = submissions.filter(s => s.type === 'answer');

  const displaySubmissions = gameType === 'memeMachine' ? memes : 
                             gameType === 'vibeCode' ? codes : 
                             answers;

  if (displaySubmissions.length === 0) {
    return (
      <div className="bg-white rounded-2xl p-8 text-center">
        <div className="text-6xl mb-4">üì≠</div>
        <h3 className="text-xl font-bold text-gray-400">No submissions yet</h3>
        <p className="text-gray-400">Waiting for players to submit...</p>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-2xl p-4">
      <h3 className="text-lg font-bold text-[#3d5a4c] mb-4">
        üé® SUBMISSIONS ({displaySubmissions.length})
      </h3>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {displaySubmissions.map((sub, i) => (
          <div key={i} className="bg-[#f5f3ef] rounded-xl overflow-hidden shadow-lg hover:scale-105 transition-transform">
            {sub.imageUrl && (
              <div className="relative">
                <img 
                  src={sub.imageUrl} 
                  alt="" 
                  className="w-full h-40 object-cover"
                />
                {sub.caption && (
                  <div 
                    className="absolute bottom-0 left-0 right-0 p-2 text-center text-white text-sm font-bold"
                    style={{
                      fontFamily: sub.captionStyle?.font || 'Impact, sans-serif',
                      textShadow: '1px 1px 2px rgba(0,0,0,0.8)',
                      backgroundColor: 'rgba(0,0,0,0.6)',
                    }}
                  >
                    {sub.caption}
                  </div>
                )}
              </div>
            )}

            <div className="p-3">
              <div className="font-bold text-[#3d5a4c] truncate">{sub.playerName}</div>
              {sub.issue && (
                <div className="text-xs text-gray-500">#{sub.issue.replace(/\s+/g, '')}</div>
              )}

              {/* Reaction counts */}
              <div className="flex gap-2 mt-2 flex-wrap">
                {Object.entries(reactions[sub.orderId] || {}).map(([type, count]) => (
                  <span key={type} className="text-xs bg-gray-200 px-2 py-1 rounded-full">
                    {type} {count}
                  </span>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Main Dashboard Component
const FacilitatorDashboard = ({ gameCode, room, onClose }) => {
  const [timeLeft, setTimeLeft] = useState(0);
  const [totalTime, setTotalTime] = useState(0);
  const [activities, setActivities] = useState([]);

  // Calculate stats
  const players = room?.players || [];
  const submissions = room?.submissions || [];
  const submittedCount = players.filter(p => 
    submissions.some(s => s.playerId === p.id)
  ).length;

  // Timer effect
  useEffect(() => {
    if (room?.phaseEndTime) {
      const total = room?.phaseDuration || 300;
      setTotalTime(total);

      const interval = setInterval(() => {
        const remaining = Math.max(0, Math.floor((room.phaseEndTime - Date.now()) / 1000));
        setTimeLeft(remaining);
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [room?.phaseEndTime, room?.phaseDuration]);

  // Track activities
  useEffect(() => {
    if (submissions.length > 0) {
      const newActivities = submissions.slice(-10).map(sub => ({
        icon: sub.type === 'meme' ? 'üé®' : sub.type === 'code' ? 'üíª' : '‚úÖ',
        player: sub.playerName,
        action: `submitted a ${sub.type}`,
        time: new Date(sub.submittedAt).toLocaleTimeString(),
      }));
      setActivities(prev => [...prev.slice(-20), ...newActivities]);
    }
  }, [submissions.length]);

  // Add player join activities
  useEffect(() => {
    players.forEach(player => {
      if (player.joinedAt && Date.now() - player.joinedAt < 5000) {
        setActivities(prev => [...prev, {
          icon: 'üëã',
          player: player.name,
          action: 'joined the game',
          time: new Date().toLocaleTimeString(),
        }]);
      }
    });
  }, [players.length]);

  return (
    <div className="fixed inset-0 bg-[#1a1a2e] z-50 overflow-auto">
      {/* Header */}
      <div className="bg-gradient-to-r from-[#3d5a4c] to-[#48a89a] p-4">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <div className="flex items-center gap-4">
            <div className="text-4xl">üì∫</div>
            <div>
              <h1 className="text-2xl font-black text-white">FACILITATOR DASHBOARD</h1>
              <div className="text-white/70">
                Room: <span className="font-mono font-bold text-[#d4a84b]">{gameCode}</span>
                <span className="mx-2">‚Ä¢</span>
                {room?.gameType?.toUpperCase().replace(/([A-Z])/g, ' $1')}
              </div>
            </div>
          </div>

          <Button 
            onClick={onClose}
            variant="secondary"
            className="bg-white/20 text-white hover:bg-white/30"
          >
            ‚úï Close Dashboard
          </Button>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto p-4 space-y-4">
        {/* Top Row: Timer + Stats + Controls */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* Timer */}
          <BigTimer 
            seconds={timeLeft} 
            totalSeconds={totalTime}
            phase={room?.phase}
          />

          {/* Quick Stats */}
          <div className="grid grid-cols-2 gap-3">
            <StatsCard 
              icon="üë•" 
              label="Players" 
              value={players.length}
              color="bg-[#48a89a]"
            />
            <StatsCard 
              icon="‚úÖ" 
              label="Submitted" 
              value={`${submittedCount}/${players.length}`}
              color="bg-[#d4a84b]"
            />
            <StatsCard 
              icon="üéØ" 
              label="Round" 
              value={room?.round || 1}
              color="bg-purple-600"
            />
            <StatsCard 
              icon="üìä" 
              label="Total Subs" 
              value={submissions.length}
              color="bg-blue-600"
            />
          </div>

          {/* Phase Controls */}
          <PhaseControls 
            gameCode={gameCode}
            currentPhase={room?.phase}
            gameType={room?.gameType}
          />
        </div>

        {/* Middle Row: Players + Activity + Leaderboard */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <PlayerGrid players={players} />
          <ActivityFeed activities={activities} />
          <Leaderboard players={players} />
        </div>

        {/* Bottom: Submission Gallery */}
        <SubmissionGallery 
          submissions={submissions}
          reactions={room?.reactions || {}}
          gameType={room?.gameType}
        />

