// SPOT THE FAKE - Enhanced Game Component
// Bold video-game style UI with werewolf finale
import React, { useState, useEffect } from 'react';
import { Card, Button, Timer, PlayerList, Badge, ProgressSteps } from './components';
import { educationalContent, getDiscussionPrompts, getQuizQuestions } from './educational-content';
import { updateGamePhase, updatePlayerScore, submitToGame } from './firebase';

// Example images that match real-world cases
const exampleImages = {
  'pentagon-explosion': 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?w=600&h=400&fit=crop', // Explosion/smoke
  'pope-puffer': 'https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=600&h=400&fit=crop', // Portrait that could be modified
  'trump-arrest': 'https://images.unsplash.com/photo-1529107386315-e1a2ed48a620?w=600&h=400&fit=crop', // Crowd scene
  'celebrity-deepfakes': 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?w=600&h=400&fit=crop', // Portrait
};

const imageDatabase = {
  round1: [
    { id: 'r1-1', url: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=600', isAI: false, category: 'people', hint: 'Look at the skin texture and eye reflections' },
    { id: 'r1-2', url: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600', isAI: false, category: 'landscape', hint: 'Check the atmospheric perspective' },
    { id: 'r1-3', url: 'https://images.unsplash.com/photo-1574158622682-e40e69881006?w=600', isAI: false, category: 'animals', hint: 'Examine the fur texture' },
    { id: 'r1-4', url: 'https://images.unsplash.com/photo-1486312338219-ce68d2c6f44d?w=600', isAI: false, category: 'objects', hint: 'Check for readable text' },
    { id: 'r1-5', url: 'https://images.unsplash.com/photo-1517841905240-472988babdf9?w=600', isAI: false, category: 'people', hint: 'Look at the hair edges' },
  ],
  round2: [
    { id: 'r2-1', url: 'https://images.unsplash.com/photo-1449824913935-59a10b8d2000?w=600', isEdited: false, category: 'architecture', hint: 'Check perspective lines' },
    { id: 'r2-2', url: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?w=600', isEdited: false, category: 'food', hint: 'Look at reflections' },
    { id: 'r2-3', url: 'https://images.unsplash.com/photo-1533738363-b7f9aef128ce?w=600', isEdited: false, category: 'animals', hint: 'Check eye details' },
  ],
  // Werewolf game images - mix of real and AI
  werewolf: [
    { id: 'ww-1', url: 'https://images.unsplash.com/photo-1534528741775-53994a69daeb?w=400&h=400&fit=crop', isAI: true },
    { id: 'ww-2', url: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=400&fit=crop', isAI: false },
    { id: 'ww-3', url: 'https://images.unsplash.com/photo-1544005313-94ddf0286df2?w=400&h=400&fit=crop', isAI: false },
    { id: 'ww-4', url: 'https://images.unsplash.com/photo-1517841905240-472988babdf9?w=400&h=400&fit=crop', isAI: true },
    { id: 'ww-5', url: 'https://images.unsplash.com/photo-1506794778202-cad84cf45f1d?w=400&h=400&fit=crop', isAI: false },
    { id: 'ww-6', url: 'https://images.unsplash.com/photo-1500648767791-00dcc994a43e?w=400&h=400&fit=crop', isAI: true },
    { id: 'ww-7', url: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=400&h=400&fit=crop', isAI: false },
    { id: 'ww-8', url: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=400&h=400&fit=crop', isAI: false },
  ]
};

// Enhanced examples with actual images
const enhancedExamples = [
  {
    id: "pentagon-explosion",
    title: "The Pentagon 'Explosion' (May 2023)",
    description: "An AI-generated image showing an explosion near the Pentagon went viral on Twitter, briefly causing stock market fluctuations.",
    impact: "The S&P 500 briefly dropped, demonstrating how AI fakes can affect financial markets in minutes.",
    detection: "Telltale signs included: an impossible fence structure, inconsistent smoke patterns, and buildings that didn't match the actual Pentagon layout.",
    category: "misinformation",
    image: "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?w=600&h=400&fit=crop",
    imageCaption: "Example of AI-generated smoke/explosion imagery"
  },
  {
    id: "pope-puffer",
    title: "The Pope in a Puffer Jacket (March 2023)",
    description: "A Midjourney-generated image of Pope Francis wearing a stylish white puffer jacket went massively viral, fooling millions.",
    impact: "Shared over 20 million times before being identified as AI-generated.",
    detection: "Close examination revealed: distorted crucifix, irregular glasses frames, and hands that don't quite look right.",
    category: "viral-hoax",
    image: "https://images.unsplash.com/photo-1533636721434-0e2d61030955?w=600&h=400&fit=crop",
    imageCaption: "AI often creates unrealistic fabric textures and accessories"
  },
  {
    id: "political-fakes",
    title: "AI in Political Campaigns (2024)",
    description: "AI-generated images and audio have been used in political campaigns worldwide, from fake endorsements to manipulated speeches.",
