// SPOT THE FAKE - Game Component
import React, { useState, useEffect } from 'react';
import { Card, Button, Timer, PlayerList, Badge, ScoreDisplay, ProgressSteps } from './components';
import { educationalContent, getDiscussionPrompts, getQuizQuestions } from './educational-content';
import { updateGamePhase, updatePlayerScore } from './firebase';

const imageDatabase = {
  round1: [
    { id: 'r1-1', url: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=600', isAI: false, category: 'people', hint: 'Look at the skin texture and eye reflections' },
    { id: 'r1-2', url: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=600', isAI: false, category: 'landscape', hint: 'Check the atmospheric perspective' },
    { id: 'r1-3', url: 'https://images.unsplash.com/photo-1574158622682-e40e69881006?w=600', isAI: false, category: 'animals', hint: 'Examine the fur texture' },
    { id: 'r1-4', url: 'https://images.unsplash.com/photo-1486312338219-ce68d2c6f44d?w=600', isAI: false, category: 'objects', hint: 'Check for readable text' },
    { id: 'r1-5', url: 'https://images.unsplash.com/photo-1517841905240-472988babdf9?w=600', isAI: false, category: 'people', hint: 'Look at the hair edges' },
  ],
  round2: [
    { id: 'r2-1', url: 'https://images.unsplash.com/photo-1449824913935-59a10b8d2000?w=600', isEdited: false, category: 'architecture', hint: 'Check perspective lines' },
    { id: 'r2-2', url: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?w=600', isEdited: false, category: 'food', hint: 'Look at reflections' },
    { id: 'r2-3', url: 'https://images.unsplash.com/photo-1533738363-b7f9aef128ce?w=600', isEdited: false, category: 'animals', hint: 'Check eye details' },
  ]
};

const SpotTheFake = ({ gameCode, room, userId, isHost, onBack, onOpenDashboard }) => {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showResult, setShowResult] = useState(false);
  const [localScore, setLocalScore] = useState(0);
  const [currentExample, setCurrentExample] = useState(0);
  const [quizAnswers, setQuizAnswers] = useState({});

  const phases = ['lobby', 'intro', 'round1', 'round1-debrief', 'round2', 'round2-debrief', 'ethics', 'legal', 'quiz', 'results'];
  const currentPhaseIndex = phases.indexOf(room?.phase);

  useEffect(() => {
    if (room?.phase === 'round1') {
      setImages([...imageDatabase.round1].sort(() => Math.random() - 0.5));
      setCurrentIndex(0);
      setSelectedAnswer(null);
      setShowResult(false);
    } else if (room?.phase === 'round2') {
      setImages([...imageDatabase.round2].sort(() => Math.random() - 0.5));
      setCurrentIndex(0);
      setSelectedAnswer(null);
      setShowResult(false);
    }
  }, [room?.phase]);

  const handleAnswer = async (answer) => {
    if (showResult) return;
    setSelectedAnswer(answer);
    setShowResult(true);

    const currentImage = images[currentIndex];
    const isCorrect = room?.phase === 'round1' 
      ? (answer === 'ai') === currentImage.isAI
      : (answer === 'edited') === currentImage.isEdited;

    if (isCorrect) {
      setLocalScore(s => s + 10);
      await updatePlayerScore(gameCode, userId, 10);
    }

    setTimeout(() => {
      if (currentIndex < images.length - 1) {
        setCurrentIndex(i => i + 1);
        setSelectedAnswer(null);
        setShowResult(false);
      } else if (isHost) {
        updateGamePhase(gameCode, room?.phase === 'round1' ? 'round1-debrief' : 'round2-debrief');
      }
    }, 3000);
  };

  const renderPhase = () => {
    switch (room?.phase) {
      case 'lobby':
        return (
          <div className="space-y-6">
            <Card className="text-center">
              <div className="text-6xl mb-4">ðŸ”</div>
              <h2 className="text-2xl font-bold text-[#3d5a4c] mb-2">Spot the Fake</h2>
              <p className="text-[#6b7c74] mb-6">
                Learn to identify AI-generated and manipulated images through hands-on practice.
              </p>
              <div className="bg-[#f5f3ef] rounded-xl p-4 mb-6 text-left">
                <h3 className="font-semibold text-[#3d5a4c] mb-2">What You'll Learn:</h3>
                <ul className="space-y-1 text-sm text-[#6b7c74]">
                  <li>âœ“ How to spot AI-generated images</li>
                  <li>âœ“ Real-world examples of AI image misuse</li>
                  <li>âœ“ Detection techniques used by experts</li>
                  <li>âœ“ Ethical implications of AI images</li>
                  <li>âœ“ Legal landscape around deepfakes</li>
                </ul>
              </div>
              <PlayerList players={room?.players} currentUserId={userId} />
            </Card>

            {isHost && (
              <div className="flex gap-4 justify-center">
                <Button onClick={() => updateGamePhase(gameCode, 'intro')} size="lg">
                  Start Game â†’
                </Button>
                <Button variant="secondary" onClick={onOpenDashboard}>
                  ðŸ“Š Open Dashboard
                </Button>
              </div>
            )}
          </div>
        );

      case 'intro':
        const examples = educationalContent.realWorldExamples;
        const example = examples[currentExample];

        return (
          <div className="space-y-6">
            <Card>
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-bold text-[#3d5a4c]">The Reality of AI Fakes</h2>
                <div className="text-sm text-[#6b7c74]">
                  Example {currentExample + 1} of {examples.length}
                </div>
              </div>

              {currentExample === 0 && (
                <div className="bg-[#3d5a4c] text-white rounded-xl p-4 mb-6">
                  <h3 className="font-semibold mb-3">AI Images by the Numbers:</h3>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
                    {educationalContent.introduction.keyStats.map((stat, i) => (
                      <div key={i} className="text-sm">{stat}</div>
                    ))}
                  </div>
                </div>
              )}

              <div className="bg-[#f5f3ef] rounded-xl p-6 mb-6">
                <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                  example.category === 'political' ? 'bg-red-100 text-red-800' :
                  example.category === 'misinformation' ? 'bg-yellow-100 text-yellow-800' :
                  example.category === 'harassment' ? 'bg-purple-100 text-purple-800' :
                  'bg-blue-100 text-blue-800'
                }`}>
                  {example.category}
                </span>
                <h3 className="text-lg font-bold text-[#3d5a4c] mt-3 mb-2">{example.title}</h3>
                <p className="text-[#6b7c74] mb-4">{example.description}</p>

                <div className="space-y-3">
                  <div className="p-3 bg-red-50 rounded-lg">
                    <span className="font-semibold text-red-800">Impact: </span>
                    <span className="text-red-700">{example.impact}</span>
                  </div>
                  <div className="p-3 bg-green-50 rounded-lg">
                    <span className="font-semibold text-green-800">How It Was Detected: </span>
                    <span className="text-green-700">{example.detection}</span>
                  </div>
                </div>
              </div>

              <div className="flex justify-between">
                <Button 
                  variant="ghost" 
                  onClick={() => setCurrentExample(i => Math.max(0, i - 1))}
                  disabled={currentExample === 0}
                >
                  â† Previous
                </Button>
                {currentExample < examples.length - 1 ? (
                  <Button onClick={() => setCurrentExample(i => i + 1)}>
                    Next Example â†’
                  </Button>
                ) : isHost && (
                  <Button onClick={() => updateGamePhase(gameCode, 'round1')}>
                    Start Round 1 â†’
                  </Button>
                )}
              </div>
            </Card>
          </div>
        );

      case 'round1':
      case 'round2':
        const currentImage = images[currentIndex];
        if (!currentImage) return <div className="text-center p-8">Loading images...</div>;

        const tips = educationalContent.detectionTechniques[currentImage.category === 'people' ? 'faces' : 'backgrounds'];

        return (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-lg font-bold text-[#3d5a4c]">
                  {room?.phase === 'round1' ? 'Round 1: Real or AI?' : 'Round 2: Original or Edited?'}
                </h2>
                <div className="text-sm text-[#6b7c74]">
                  Image {currentIndex + 1} of {images.length}
                </div>
              </div>
              <div className="flex items-center gap-4">
                <ScoreDisplay score={localScore} />
                <Timer seconds={30} paused={showResult || room?.isPaused} extension={room?.timerExtension} />
              </div>
            </div>

            <Card>
              <div className="aspect-video bg-[#f5f3ef] rounded-xl overflow-hidden mb-4 relative">
                <img 
                  src={currentImage.url} 
                  alt="Identify this" 
                  className="w-full h-full object-cover"
                />
                {showResult && (
                  <div className={`absolute inset-0 flex items-center justify-center ${
                    (room?.phase === 'round1' ? (selectedAnswer === 'ai') === currentImage.isAI : (selectedAnswer === 'edited') === currentImage.isEdited)
                      ? 'bg-green-500/80' : 'bg-red-500/80'
                  }`}>
                    <div className="text-white text-center">
                      <div className="text-6xl mb-2">
                        {(room?.phase === 'round1' ? (selectedAnswer === 'ai') === currentImage.isAI : (selectedAnswer === 'edited') === currentImage.isEdited)
                          ? 'âœ“' : 'âœ—'}
                      </div>
                      <div className="text-xl font-bold">
                        {(room?.phase === 'round1' ? (selectedAnswer === 'ai') === currentImage.isAI : (selectedAnswer === 'edited') === currentImage.isEdited)
                          ? 'Correct! +10 points' : 'Not quite!'}
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {!showResult && (
                <>
                  <div className="bg-[#f5f3ef] rounded-lg p-3 mb-4 text-center">
                    <span className="text-[#6b7c74]">ðŸ’¡ Hint: </span>
                    <span className="text-[#3d5a4c]">{currentImage.hint}</span>
                  </div>

                  <div className="flex gap-4 justify-center">
                    {room?.phase === 'round1' ? (
                      <>
                        <Button onClick={() => handleAnswer('real')} size="lg" variant="secondary" className="flex-1 max-w-xs">
                          <span className="text-2xl mr-2">ðŸ“·</span> Real Photo
                        </Button>
                        <Button onClick={() => handleAnswer('ai')} size="lg" variant="accent" className="flex-1 max-w-xs">
                          <span className="text-2xl mr-2">ðŸ¤–</span> AI Generated
                        </Button>
                      </>
                    ) : (
                      <>
                        <Button onClick={() => handleAnswer('original')} size="lg" variant="secondary" className="flex-1 max-w-xs">
                          <span className="text-2xl mr-2">âœ“</span> Original
                        </Button>
                        <Button onClick={() => handleAnswer('edited')} size="lg" variant="accent" className="flex-1 max-w-xs">
                          <span className="text-2xl mr-2">âœ‚ï¸</span> Edited
                        </Button>
                      </>
                    )}
                  </div>
                </>
              )}
            </Card>

            <Card className="bg-[#f5f3ef]">
              <h3 className="font-semibold text-[#3d5a4c] mb-3">
                ðŸ” Detection Tips: {tips?.title}
              </h3>
              <div className="space-y-2">
                {tips?.techniques.slice(0, 3).map((tech, i) => (
                  <div key={i} className="bg-white p-3 rounded-lg">
                    <div className="flex justify-between items-start">
                      <span className="font-medium text-[#3d5a4c]">{tech.name}</span>
                      <span className={`px-2 py-0.5 rounded text-xs ${
                        tech.reliability === 'Very High' || tech.reliability === 'High' ? 'bg-green-100 text-green-800' :
                        tech.reliability === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {tech.reliability}
                      </span>
                    </div>
                    <p className="text-sm text-[#6b7c74] mt-1">{tech.description}</p>
                  </div>
                ))}
              </div>
            </Card>
          </div>
        );

      case 'round1-debrief':
      case 'round2-debrief':
        const debriefPrompts = getDiscussionPrompts(room?.phase === 'round1-debrief' ? 'round1' : 'round2');

        return (
          <div className="space-y-6">
            <Card>
              <h2 className="text-xl font-bold text-[#3d5a4c] mb-4">
                ðŸŽ¯ {room?.phase === 'round1-debrief' ? 'Round 1' : 'Round 2'} Complete!
              </h2>

              <div className="bg-[#f5f3ef] rounded-xl p-4 mb-6">
                <h3 className="font-semibold text-[#3d5a4c] mb-3">Current Standings</h3>
                <div className="space-y-2">
                  {[...room?.players || []].sort((a, b) => (b.score || 0) - (a.score || 0)).slice(0, 5).map((player, i) => (
                    <div key={player.id} className={`flex justify-between p-3 rounded-lg ${i === 0 ? 'bg-[#48a89a]/20' : 'bg-white'}`}>
                      <span>{i === 0 && 'ðŸ‘‘ '}{player.name}</span>
                      <span className="font-bold">{player.score || 0} pts</span>
                    </div>
                  ))}
                </div>
              </div>

              <div className="mb-6">
                <h3 className="font-semibold text-[#3d5a4c] mb-3">ðŸ’¬ Discussion Questions</h3>
                <div className="space-y-3">
                  {debriefPrompts.map((prompt, i) => (
                    <div key={i} className="p-4 bg-[#48a89a]/10 rounded-lg">
                      <p className="text-[#3d5a4c]">{prompt}</p>
                    </div>
                  ))}
                </div>
              </div>

              {isHost && (
                <Button 
                  onClick={() => updateGamePhase(gameCode, room?.phase === 'round1-debrief' ? 'round2' : 'ethics')}
                  size="lg"
                >
                  {room?.phase === 'round1-debrief' ? 'Start Round 2 â†’' : 'Continue to Ethics â†’'}
                </Button>
              )}
            </Card>
          </div>
        );

      case 'ethics':
        const ethicsContent = educationalContent.ethics;

        return (
          <div className="space-y-6">
            <Card>
              <h2 className="text-xl font-bold text-[#3d5a4c] mb-2">âš–ï¸ {ethicsContent.title}</h2>
              <p className="text-[#6b7c74] mb-6">
                Now that you can spot AI images, let's discuss when their creation is acceptable vs. harmful.
              </p>

              <div className="space-y-6">
                {ethicsContent.questions.map((q, i) => (
                  <div key={i} className="border border-[#e2e0dc] rounded-xl overflow-hidden">
                    <div className="bg-[#3d5a4c] text-white p-4">
                      <h3 className="font-semibold">{q.question}</h3>
                    </div>
                    <div className="p-4">
                      <p className="text-[#6b7c74] mb-4">{q.discussion}</p>
                      <div className="space-y-2">
                        {q.scenarios.map((scenario, j) => (
                          <div key={j} className={`p-3 rounded-lg text-sm ${
                            scenario.startsWith('âœ…') ? 'bg-green-50 text-green-800' :
                            scenario.startsWith('âš ï¸') ? 'bg-yellow-50 text-yellow-800' :
                            scenario.startsWith('âŒ') ? 'bg-red-50 text-red-800' :
                            'bg-[#f5f3ef] text-[#6b7c74]'
                          }`}>
                            {scenario}
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {isHost && (
                <Button onClick={() => updateGamePhase(gameCode, 'legal')} className="mt-6" size="lg">
                  Continue to Legal Implications â†’
                </Button>
              )}
            </Card>
          </div>
        );

      case 'legal':
        const legalContent = educationalContent.legal;

        return (
          <div className="space-y-6">
            <Card>
              <h2 className="text-xl font-bold text-[#3d5a4c] mb-2">âš–ï¸ {legalContent.title}</h2>
              <p className="text-[#6b7c74] mb-6">{legalContent.overview}</p>

              <div className="space-y-4">
                {legalContent.categories.map((cat, i) => (
                  <div key={i} className="border border-[#e2e0dc] rounded-xl p-4">
                    <div className="flex justify-between items-start mb-2">
                      <h3 className="font-semibold text-[#3d5a4c]">{cat.area}</h3>
                      <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                        cat.status.includes('Illegal') ? 'bg-red-100 text-red-800' :
                        cat.status.includes('Restricted') ? 'bg-yellow-100 text-yellow-800' :
                        'bg-blue-100 text-blue-800'
                      }`}>
                        {cat.status}
                      </span>
                    </div>
                    <p className="text-sm text-[#6b7c74] mb-2">{cat.details}</p>
                    <p className="text-xs text-red-600">Penalties: {cat.penalties}</p>
                  </div>
                ))}
              </div>

              {isHost && (
                <Button onClick={() => updateGamePhase(gameCode, 'quiz')} className="mt-6" size="lg">
                  Take the Quiz â†’
                </Button>
              )}
            </Card>
          </div>
        );

      case 'quiz':
        const quizQuestions = getQuizQuestions(3);
        const answeredAll = Object.keys(quizAnswers).length === quizQuestions.length;

        return (
          <div className="space-y-6">
            <Card>
              <h2 className="text-xl font-bold text-[#3d5a4c] mb-4">ðŸ“ Knowledge Check</h2>

              <div className="space-y-6">
                {quizQuestions.map((q, i) => (
                  <div key={i} className="border border-[#e2e0dc] rounded-xl p-4">
                    <p className="font-medium text-[#3d5a4c] mb-3">{i + 1}. {q.question}</p>
                    <div className="space-y-2">
                      {q.options.map((option, j) => (
                        <button
                          key={j}
                          onClick={() => setQuizAnswers(prev => ({ ...prev, [i]: j }))}
                          className={`w-full p-3 rounded-lg text-left transition-all ${
                            quizAnswers[i] === j 
                              ? quizAnswers[i] === q.correct 
                                ? 'bg-green-100 border-2 border-green-500' 
                                : 'bg-red-100 border-2 border-red-500'
                              : 'bg-[#f5f3ef] hover:bg-[#e2e0dc]'
                          }`}
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                    {quizAnswers[i] !== undefined && (
                      <div className={`mt-3 p-3 rounded-lg ${quizAnswers[i] === q.correct ? 'bg-green-50' : 'bg-yellow-50'}`}>
                        <p className="text-sm">{q.explanation}</p>
                      </div>
                    )}
                  </div>
                ))}
              </div>

              {isHost && answeredAll && (
                <Button onClick={() => updateGamePhase(gameCode, 'results')} className="mt-6" size="lg">
                  See Final Results â†’
                </Button>
              )}
            </Card>
          </div>
        );

      case 'results':
        const winner = room?.players?.reduce((a, b) => (a.score || 0) > (b.score || 0) ? a : b, { score: 0 });

        return (
          <div className="space-y-6 text-center">
            <h2 className="text-3xl font-bold text-[#3d5a4c]">ðŸ† Game Complete!</h2>

            <Card>
              <h3 className="font-semibold text-[#3d5a4c] mb-4">Final Standings</h3>
              <div className="space-y-2">
                {[...room?.players || []].sort((a, b) => (b.score || 0) - (a.score || 0)).map((player, i) => (
                  <div key={player.id} className={`flex justify-between p-4 rounded-lg ${i === 0 ? 'bg-[#d4a84b]/20' : 'bg-[#f5f3ef]'}`}>
                    <span className="flex items-center gap-2">
                      {i === 0 && 'ðŸ¥‡'}
                      {i === 1 && 'ðŸ¥ˆ'}
                      {i === 2 && 'ðŸ¥‰'}
                      {player.name}
                    </span>
                    <span className="font-bold">{player.score || 0} pts</span>
                  </div>
                ))}
              </div>
            </Card>

            {winner && winner.score > 0 && (
              <Badge 
                game="spotTheFake" 
                playerName={winner.name}
                onExport={() => alert('Badge exported!')}
              />
            )}

            <Card className="text-left">
              <h3 className="font-semibold text-[#3d5a4c] mb-3">ðŸŽ“ Key Takeaways</h3>
              <ul className="space-y-2 text-[#6b7c74]">
                <li>â€¢ Always verify images before sharing, especially sensational ones</li>
                <li>â€¢ Check hands, text, and edges for AI artifacts</li>
                <li>â€¢ Consider the source and context of images</li>
                <li>â€¢ Be aware of the ethical and legal implications</li>
                <li>â€¢ When in doubt, use reverse image search</li>
              </ul>
            </Card>

            <Button onClick={onBack} variant="secondary" size="lg">
              Back to Games
            </Button>
          </div>
        );

      default:
        return <div className="text-center p-8">Unknown phase: {room?.phase}</div>;
    }
  };

  return (
    <div className="min-h-screen bg-[#f5f3ef] p-4">
      <div className="max-w-4xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <button onClick={onBack} className="text-[#6b7c74] hover:text-[#3d5a4c] flex items-center gap-2">
            â† Back
          </button>
          <div className="flex items-center gap-4">
            {isHost && (
              <Button variant="ghost" size="sm" onClick={onOpenDashboard}>
                ðŸ“Š Dashboard
              </Button>
            )}
            <div className="text-sm text-[#6b7c74]">
              Code: <span className="font-mono font-bold text-[#3d5a4c]">{gameCode}</span>
            </div>
          </div>
        </div>

        {currentPhaseIndex > 0 && currentPhaseIndex < phases.length - 1 && (
          <div className="mb-6">
            <ProgressSteps 
              steps={phases} 
              currentStep={currentPhaseIndex}
              onStepClick={isHost ? (i) => updateGamePhase(gameCode, phases[i]) : null}
            />
          </div>
        )}

        {renderPhase()}
      </div>
    </div>
  );
};

export default SpotTheFake;
