// VIBE CODE CHALLENGE - Redesigned Game Component
// Features: BYTE AI mentor, animated villains, mockup step, code streaming animation

import React, { useState } from 'react';
import { Card, Button, Timer, PlayerList, Alert, Badge, CodePreview, CodeStreamingAnimation } from './components';
import { updateGamePhase, submitToGame, submitVote } from './firebase';
import { generateInitialCode, generateMockup, iterateCode, polishCode, generateAppDescription, hasApiKey } from './ai-services';

// BYTE - The AI Mentor Character
const ByteMentor = ({ message, mood = 'happy', animate = true }) => {
  const moods = {
    happy: 'ðŸ˜Š',
    thinking: 'ðŸ¤”',
    excited: 'ðŸŽ‰',
    encouraging: 'ðŸ’ª',
    warning: 'âš ï¸',
    coding: 'ðŸ’»',
  };

  return (
    <div className={`flex items-start gap-4 p-4 bg-gradient-to-r from-[#6b8cce]/20 to-[#5070b0]/20 rounded-2xl border-2 border-[#6b8cce] ${animate ? 'animate-fadeIn' : ''}`}>
      <div className="flex-shrink-0">
        <div className="w-16 h-16 bg-gradient-to-br from-[#6b8cce] to-[#5070b0] rounded-full flex items-center justify-center text-3xl shadow-lg">
          ðŸ¤–
        </div>
        <div className="text-center mt-1 text-xs font-bold text-[#6b8cce]">BYTE</div>
      </div>
      <div className="flex-1">
        <div className="flex items-center gap-2 mb-1">
          <span className="font-bold text-[#3d5a4c] text-lg">BYTE says:</span>
          <span className="text-xl">{moods[mood]}</span>
        </div>
        <p className="text-[#3d5a4c] text-lg leading-relaxed">{message}</p>
      </div>
    </div>
  );
};

// Villain Characters
const VillainBanner = ({ villains = ['chaos', 'complexity', 'confusion'] }) => {
  const villainData = {
    chaos: { name: 'CHAOS', emoji: 'ðŸŒ€', color: 'from-red-500 to-orange-500' },
    complexity: { name: 'COMPLEXITY', emoji: 'ðŸ•¸ï¸', color: 'from-purple-500 to-pink-500' },
    confusion: { name: 'CONFUSION', emoji: 'â“', color: 'from-gray-600 to-gray-800' },
    bugs: { name: 'BUGS', emoji: 'ðŸ›', color: 'from-green-600 to-lime-500' },
    scope: { name: 'SCOPE CREEP', emoji: 'ðŸ‘¹', color: 'from-amber-600 to-yellow-500' },
  };

  return (
    <div className="flex justify-center gap-4 my-6">
      {villains.map((v, i) => {
        const villain = villainData[v];
        return (
          <div 
            key={i}
            className={`bg-gradient-to-br ${villain.color} p-3 rounded-xl text-white text-center transform hover:scale-110 transition-all shadow-lg animate-pulse`}
            style={{ animationDelay: `${i * 0.2}s` }}
          >
            <div className="text-3xl mb-1">{villain.emoji}</div>
            <div className="text-xs font-bold">{villain.name}</div>
          </div>
        );
      })}
    </div>
  );
};

// Big Bold Header Component
const GameHeader = ({ title, subtitle, icon }) => (
  <div className="text-center mb-8">
    <div className="text-6xl mb-4">{icon}</div>
    <h1 className="text-4xl md:text-5xl font-black text-[#3d5a4c] mb-2 tracking-tight">
      {title}
    </h1>
    {subtitle && (
      <p className="text-xl text-[#6b7c74] font-medium">{subtitle}</p>
    )}
  </div>
);

// Input Step Component with BYTE examples
const InputStep = ({ label, placeholder, value, onChange, byteMessage, byteExamples, hint }) => (
  <div className="space-y-4">
    <ByteMentor message={byteMessage} mood="encouraging" />

    <div className="bg-white rounded-2xl p-6 border-2 border-[#e2e0dc]">
      <label className="block text-xl font-bold text-[#3d5a4c] mb-4">{label}</label>

      {byteExamples && (
        <div className="mb-4 p-4 bg-[#f5f3ef] rounded-xl">
          <div className="text-sm font-medium text-[#6b7c74] mb-2">ðŸ’¡ BYTE's Examples (click to use):</div>
          <div className="space-y-2">
            {byteExamples.map((ex, i) => (
              <button 
                key={i}
                type="button"
                className="block w-full text-left text-[#3d5a4c] text-sm italic cursor-pointer hover:text-[#6b8cce] hover:bg-[#6b8cce]/10 p-2 rounded transition-colors"
                onClick={() => onChange(ex)}
              >
                "{ex}"
              </button>
            ))}
          </div>
        </div>
      )}

      <textarea
        value={value}
        onChange={(e) => onChange(e.target.value)}
        placeholder={placeholder}
        className="w-full px-4 py-3 rounded-xl border-2 border-[#e2e0dc] focus:border-[#6b8cce] focus:outline-none focus:ring-2 focus:ring-[#6b8cce]/20 transition-all resize-none text-lg"
        rows={3}
      />

      {hint && (
        <p className="mt-2 text-sm text-[#6b7c74]">ðŸ’¡ {hint}</p>
      )}
    </div>
  </div>
);

// Mockup Display Component
const MockupDisplay = ({ mockup }) => {
  if (!mockup) return null;

  return (
    <div className="space-y-6">
      {/* ASCII Mockup */}
      <div className="bg-gray-900 rounded-xl p-6 font-mono text-green-400 text-sm overflow-x-auto">
        <div className="text-gray-500 mb-2 text-xs">ðŸ“ LAYOUT PREVIEW</div>
        <pre className="whitespace-pre">{mockup.asciiMockup}</pre>
      </div>

      {/* Color Scheme */}
      <div className="bg-white rounded-xl p-6 border-2 border-[#e2e0dc]">
        <h4 className="font-bold text-[#3d5a4c] mb-4">ðŸŽ¨ Color Scheme</h4>
        <div className="flex flex-wrap gap-3">
          {mockup.colorScheme?.map((c, i) => (
            <div key={i} className="flex items-center gap-2 bg-[#f5f3ef] rounded-lg p-3">
              <div 
                className="w-8 h-8 rounded-lg shadow-inner border border-gray-200"
                style={{ backgroundColor: c.color }}
              />
              <div>
                <div className="text-sm font-medium text-[#3d5a4c]">{c.name}</div>
                <div className="text-xs text-[#6b7c74]">{c.usage}</div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Components */}
      <div className="bg-white rounded-xl p-6 border-2 border-[#e2e0dc]">
        <h4 className="font-bold text-[#3d5a4c] mb-4">ðŸ§© Components</h4>
        <div className="space-y-2">
          {mockup.components?.map((comp, i) => (
            <div key={i} className="flex items-start gap-3 p-3 bg-[#f5f3ef] rounded-lg">
              <div className="w-8 h-8 bg-[#6b8cce] rounded-lg flex items-center justify-center text-white font-bold">
                {i + 1}
              </div>
              <div>
                <div className="font-medium text-[#3d5a4c]">{comp.name}</div>
                <div className="text-sm text-[#6b7c74]">{comp.description}</div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* User Flow */}
      <div className="bg-white rounded-xl p-6 border-2 border-[#e2e0dc]">
        <h4 className="font-bold text-[#3d5a4c] mb-4">ðŸš€ User Flow</h4>
        <div className="flex flex-wrap items-center gap-2">
          {mockup.userFlow?.map((step, i) => (
            <React.Fragment key={i}>
              <div className="bg-[#48a89a] text-white px-4 py-2 rounded-full text-sm font-medium">
                {step}
              </div>
              {i < mockup.userFlow.length - 1 && (
                <span className="text-[#6b7c74]">â†’</span>
              )}
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
};

const VibeCodeChallenge = ({ gameCode, room, userId, isHost, onBack, onOpenDashboard }) => {
  // Design phase state
  const [designStep, setDesignStep] = useState(0);
  const [problem, setProblem] = useState('');
  const [solutions, setSolutions] = useState('');
  const [technology, setTechnology] = useState('');
  const [vibe, setVibe] = useState('');
  const [audience, setAudience] = useState('');
  const [goals, setGoals] = useState('');
  const [mechanics, setMechanics] = useState('');
  const [twist, setTwist] = useState('');

  // Mockup phase state
  const [mockupData, setMockupData] = useState(null);
  const [mockupFeedback, setMockupFeedback] = useState('');
  const [mockupRetries, setMockupRetries] = useState(0);

  // Code generation state
  const [generatedCode, setGeneratedCode] = useState('');
  const [isStreaming, setIsStreaming] = useState(false);
  const [streamingCode, setStreamingCode] = useState('');
  const [iterationFeedback, setIterationFeedback] = useState('');
  const [polishType, setPolishType] = useState('all');
  const [appDescription, setAppDescription] = useState(null);
  const [demoPitch, setDemoPitch] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);

  // Loading states
  const [loading, setLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('');

  // Design steps configuration
  const designSteps = [
    {
      id: 'problem',
      label: 'Problem',
      icon: 'ðŸŽ¯',
      byteMessage: "Every great app starts with a problem to solve! What frustrates you or your community? What would make life easier?",
      byteExamples: [
        "I forget to drink water throughout the day and get dehydrated",
        "Our neighborhood has no way to share info about local events",
        "Students struggle to find study partners for group projects"
      ],
      placeholder: "Describe a real problem you want to solve...",
      hint: "Be specific! The clearer the problem, the better the solution."
    },
    {
      id: 'solutions',
      label: 'Solutions',
      icon: 'ðŸ’¡',
      byteMessage: "Now let's brainstorm solutions! How could an app help solve this problem? What features would it need?",
      byteExamples: [
        "Send reminder notifications at custom intervals with motivating messages",
        "A bulletin board where neighbors can post events, news, and requests",
        "Match students by course, schedule, and study preferences"
      ],
      placeholder: "How should the app solve this problem?",
      hint: "Think about the main actions users will take."
    },
    {
      id: 'technology',
      label: 'Tech',
      icon: 'âš¡',
      byteMessage: "What technical features should we include? Think about data storage, interactivity, and special capabilities!",
      byteExamples: [
        "Save user settings locally, track daily progress with charts",
        "Real-time updates, search and filter posts, location-based sorting",
        "Profile system, messaging, calendar integration"
      ],
      placeholder: "What technical features are needed?",
      hint: "Consider: saving data, animations, notifications, etc."
    },
    {
      id: 'vibe',
      label: 'Vibe',
      icon: 'ðŸŽ¨',
      byteMessage: "What's the visual vibe? Should it feel playful, professional, calming, energetic? Colors? Style?",
      byteExamples: [
        "Fresh and energetic - bright blues and greens, bubbly animations, fun emoji",
        "Clean and modern - minimalist white with accent colors, smooth transitions",
        "Warm and friendly - earth tones, rounded shapes, welcoming illustrations"
      ],
      placeholder: "Describe the visual style and mood...",
      hint: "Think about how you want users to FEEL when using it."
    },
    {
      id: 'audience',
      label: 'Users',
      icon: 'ðŸ‘¥',
      byteMessage: "Who will use this app? Understanding your audience helps us design the right experience!",
      byteExamples: [
        "Busy professionals who need quick, easy interactions",
        "Neighbors of all ages, including seniors who need simple interfaces",
        "College students who are tech-savvy and want social features"
      ],
      placeholder: "Who is this app for?",
      hint: "Be specific about age, tech comfort, and needs."
    },
    {
      id: 'goals',
      label: 'Goals',
      icon: 'ðŸ†',
      byteMessage: "What does success look like? How will users know the app is working for them?",
      byteExamples: [
        "Users drink 8 glasses of water daily and feel better",
        "At least 10 neighbors post per week, events have better attendance",
        "Students find compatible partners within 24 hours"
      ],
      placeholder: "What outcomes should the app achieve?",
      hint: "Think measurable: what changes for users?"
    },
    {
      id: 'mechanics',
      label: 'Features',
      icon: 'âš™ï¸',
      byteMessage: "Let's list the core features - the essential things users can DO in your app!",
      byteExamples: [
        "Set daily water goal, log drinks with one tap, view weekly progress",
        "Create posts, comment, RSVP to events, search by category",
        "Create profile, browse matches, send study requests, schedule sessions"
      ],
      placeholder: "List the main features (what can users do?)",
      hint: "Focus on actions: add, create, view, track, share, etc."
    },
    {
      id: 'twist',
      label: 'Twist',
      icon: 'âœ¨',
      byteMessage: "What makes YOUR app special? Every great app has something unique that sets it apart!",
      byteExamples: [
        "A virtual plant that grows when you drink water and wilts when you don't!",
        "Karma points for helpful neighbors, monthly block party planning tool",
        "AI-powered compatibility scoring, gamified study streaks"
      ],
      placeholder: "What's the unique hook or special feature?",
      hint: "This is what makes people remember and share your app!"
    }
  ];

  const getCurrentStepValue = () => {
    const values = [problem, solutions, technology, vibe, audience, goals, mechanics, twist];
    return values[designStep] || '';
  };

  const setCurrentStepValue = (value) => {
    const setters = [setProblem, setSolutions, setTechnology, setVibe, setAudience, setGoals, setMechanics, setTwist];
    setters[designStep]?.(value);
  };

  const getDesignAnswers = () => ({
    problem,
    solutions,
    technology,
    vibe,
    audience,
    goals,
    mechanics,
    twist
  });

  // Generate mockup
  const handleGenerateMockup = async () => {
    const answers = getDesignAnswers();
    setLoading(true);
    setLoadingMessage('ðŸŽ¨ BYTE is designing your mockup...');

    try {
      const mockup = await generateMockup(answers);
      setMockupData(mockup);
      updateGamePhase(gameCode, 'mockup');
    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Regenerate mockup with feedback (only once)
  const handleRegenerateMockup = async () => {
    if (mockupRetries >= 1) {
      alert("You've used your one revision! Time to move forward with what we have.");
      return;
    }
    if (!mockupFeedback.trim()) {
      alert("Please describe what you'd like to change!");
      return;
    }

    setLoading(true);
    setLoadingMessage('ðŸ”„ BYTE is revising the mockup...');
    setMockupRetries(1);

    try {
      const answers = getDesignAnswers();
      answers.feedback = mockupFeedback;
      const mockup = await generateMockup(answers);
      setMockupData(mockup);
      setMockupFeedback('');
    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Generate code with streaming animation
  const handleGenerateCode = async () => {
    const answers = getDesignAnswers();
    setLoading(true);
    setLoadingMessage('âš¡ BYTE is writing your code...');

    try {
      const codeResponse = await generateInitialCode(
        `${answers.problem}. Style: ${answers.vibe}. For: ${answers.audience}. Unique feature: ${answers.twist}`,
        [answers.solutions, answers.mechanics, answers.technology, answers.goals]
      );

      // Extract HTML code
      const htmlMatch = codeResponse.match(/```html\n?([\s\S]*?)\n?```/) || 
                        codeResponse.match(/<!DOCTYPE[\s\S]*<\/html>/i) ||
                        [null, codeResponse];
      const cleanCode = htmlMatch[1] || codeResponse;

      // Start streaming animation
      setStreamingCode(cleanCode);
      setIsStreaming(true);
      setLoading(false);
      updateGamePhase(gameCode, 'generate');
    } catch (error) {
      alert('Error: ' + error.message);
      setLoading(false);
    }
  };

  // Handle streaming complete
  const handleStreamingComplete = () => {
    setIsStreaming(false);
    setGeneratedCode(streamingCode);
  };

  // Iterate on code
  const handleIterate = async () => {
    if (!iterationFeedback) return;
    setLoading(true);
    setLoadingMessage('ðŸ”§ Implementing your changes...');

    try {
      const newCode = await iterateCode(generatedCode, iterationFeedback, problem);
      const htmlMatch = newCode.match(/```html\n?([\s\S]*?)\n?```/) || [null, newCode];

      // Show streaming for iteration too
      setStreamingCode(htmlMatch[1] || newCode);
      setIsStreaming(true);
      setIterationFeedback('');
    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Polish code
  const handlePolish = async () => {
    setLoading(true);
    setLoadingMessage(`âœ¨ Polishing for ${polishType}...`);

    try {
      const polished = await polishCode(generatedCode, polishType);
      const htmlMatch = polished.match(/```html\n?([\s\S]*?)\n?```/) || [null, polished];
      setGeneratedCode(htmlMatch[1] || polished);
    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Generate app description
  const handleGenerateDescription = async () => {
    setLoading(true);
    setLoadingMessage('ðŸ“ Creating your pitch...');

    try {
      const desc = await generateAppDescription(generatedCode, problem);
      setAppDescription(desc);
      setDemoPitch(desc.pitchScript || '');
    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Submit app
  const handleSubmit = async () => {
    if (!generatedCode || !demoPitch) return;

    await submitToGame(gameCode, {
      type: 'vibeCode',
      problem,
      code: generatedCode,
      pitch: demoPitch,
      appName: appDescription?.name || 'My App',
      features: [solutions, mechanics],
      playerName: room?.players?.find(p => p.id === userId)?.name || 'Unknown',
    });

    setIsSubmitted(true);
  };

  // Render based on phase
  const renderPhase = () => {
    switch (room?.phase) {
      // LOBBY
      case 'lobby':
        return (
          <div className="space-y-6">
            <GameHeader 
              icon="ðŸ’»"
