// VIBE CODE CHALLENGE v2 - Enhanced Game Component
// Features: BYTE AI mentor, animated villains, huge app preview, code streaming animation
// Much larger preview windows and more interactive features

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Card, Button, Timer, PlayerList, Alert, Badge } from './components';
import { updateGamePhase, submitToGame, submitVote } from './firebase';
import { generateInitialCode, generateMockup, iterateCode, polishCode, generateAppDescription, hasApiKey } from './ai-services';

// ============================================
// BYTE - The AI Mentor Character (Enhanced)
// ============================================

const ByteMentor = ({ message, mood = 'happy', size = 'normal', animate = true }) => {
  const moods = {
    happy: { emoji: 'ðŸ˜Š', color: 'from-[#48a89a] to-[#3d8a7e]', glow: 'shadow-[#48a89a]/30' },
    thinking: { emoji: 'ðŸ¤”', color: 'from-[#6b8cce] to-[#5070b0]', glow: 'shadow-[#6b8cce]/30' },
    excited: { emoji: 'ðŸŽ‰', color: 'from-[#d4a84b] to-[#c49a3a]', glow: 'shadow-[#d4a84b]/30' },
    encouraging: { emoji: 'ðŸ’ª', color: 'from-green-500 to-green-600', glow: 'shadow-green-500/30' },
    warning: { emoji: 'âš ï¸', color: 'from-amber-500 to-orange-500', glow: 'shadow-amber-500/30' },
    coding: { emoji: 'ðŸ’»', color: 'from-purple-500 to-purple-600', glow: 'shadow-purple-500/30' },
    proud: { emoji: 'ðŸŒŸ', color: 'from-pink-500 to-rose-500', glow: 'shadow-pink-500/30' },
  };

  const currentMood = moods[mood] || moods.happy;
  const sizeClasses = {
    small: 'w-12 h-12 text-2xl',
    normal: 'w-16 h-16 text-3xl',
    large: 'w-24 h-24 text-5xl'
  };

  return (
    <div className={`flex items-start gap-4 p-5 bg-gradient-to-r ${currentMood.color}/20 rounded-2xl border-2 border-white/20 backdrop-blur-sm ${animate ? 'animate-fadeIn' : ''}`}>
      <div className="flex-shrink-0 relative">
        <div className={`${sizeClasses[size]} bg-gradient-to-br ${currentMood.color} rounded-full flex items-center justify-center shadow-lg ${currentMood.glow}`}>
          <span className="animate-bounce" style={{ animationDuration: '2s' }}>ðŸ¤–</span>
        </div>
        <div className="absolute -bottom-1 -right-1 bg-white rounded-full p-1 shadow-md">
          <span className="text-sm">{currentMood.emoji}</span>
        </div>
        <div className="text-center mt-2 text-xs font-black text-white/80 tracking-wider">BYTE</div>
      </div>
      <div className="flex-1">
        <div className="flex items-center gap-2 mb-2">
          <span className="font-black text-white text-lg">BYTE says:</span>
        </div>
        <p className="text-white/90 text-lg leading-relaxed">{message}</p>
      </div>
    </div>
  );
};

// ============================================
// VILLAIN CHARACTERS
// ============================================

const VillainBanner = ({ villains = ['chaos', 'complexity', 'confusion'], defeated = [] }) => {
  const villainData = {
    chaos: { name: 'CHAOS', emoji: 'ðŸŒ€', color: 'from-red-500 to-orange-500', desc: 'Messy ideas' },
    complexity: { name: 'COMPLEXITY', emoji: 'ðŸ•¸ï¸', color: 'from-purple-500 to-pink-500', desc: 'Overengineering' },
    confusion: { name: 'CONFUSION', emoji: 'â“', color: 'from-gray-600 to-gray-800', desc: 'Unclear goals' },
    bugs: { name: 'BUGS', emoji: 'ðŸ›', color: 'from-green-600 to-lime-500', desc: 'Code errors' },
    scope: { name: 'SCOPE CREEP', emoji: 'ðŸ‘¹', color: 'from-amber-600 to-yellow-500', desc: 'Feature bloat' },
  };

  return (
    <div className="my-8">
      <h4 className="text-center text-white/60 text-sm uppercase tracking-wider mb-4">Villains to Defeat</h4>
      <div className="flex justify-center gap-4 flex-wrap">
        {villains.map((v, i) => {
          const villain = villainData[v];
          const isDefeated = defeated.includes(v);
          return (
            <div 
              key={i}
              className={`relative bg-gradient-to-br ${villain.color} p-4 rounded-xl text-white text-center transform transition-all shadow-lg ${
                isDefeated 
                  ? 'opacity-40 scale-90 grayscale' 
                  : 'hover:scale-110 animate-pulse'
              }`}
              style={{ animationDelay: `${i * 0.2}s` }}
            >
              <div className="text-4xl mb-1">{villain.emoji}</div>
              <div className="text-xs font-bold">{villain.name}</div>
              <div className="text-[10px] opacity-70">{villain.desc}</div>
              {isDefeated && (
                <div className="absolute inset-0 flex items-center justify-center">
                  <span className="text-6xl">âœ–ï¸</span>
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

// ============================================
// ENHANCED CODE PREVIEW - MUCH LARGER
// ============================================

const EnhancedCodePreview = ({ code, onEdit, title = "App Preview", isFullWidth = false }) => {
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showCode, setShowCode] = useState(false);
  const [key, setKey] = useState(0);
  const iframeRef = useRef(null);

  useEffect(() => {
    if (code) {
      setKey(k => k + 1);
    }
  }, [code]);

  const handleRefresh = () => {
    setKey(k => k + 1);
  };

  const handleDownload = () => {
    const blob = new Blob([code], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'my-app.html';
    a.click();
    URL.revokeObjectURL(url);
  };

  if (!code) {
    return (
      <div className={`bg-gradient-to-br from-gray-800 to-gray-900 rounded-2xl overflow-hidden border-2 border-white/10 ${isFullWidth ? 'w-full' : ''}`}>
        <div className="flex justify-center items-center h-[500px]">
          <div className="text-center text-white/50">
            <div className="text-7xl mb-4 animate-pulse">ðŸ“±</div>
            <div className="text-xl">Your app will appear here</div>
            <div className="text-sm mt-2">Describe what you want to build!</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`${isFullscreen ? 'fixed inset-0 z-50 bg-black p-4' : 'relative'} ${isFullWidth ? 'w-full' : ''}`}>
      <div className={`bg-gradient-to-br from-gray-800 to-gray-900 rounded-2xl overflow-hidden border-2 border-white/10 shadow-2xl ${isFullscreen ? 'h-full flex flex-col' : ''}`}>

        {/* Preview header */}
        <div className="flex justify-between items-center px-4 py-3 bg-black/50 border-b border-white/10">
          <div className="flex items-center gap-3">
            <div className="flex gap-1.5">
              <div className="w-3 h-3 rounded-full bg-red-500"></div>
              <div className="w-3 h-3 rounded-full bg-yellow-500"></div>
              <div className="w-3 h-3 rounded-full bg-green-500"></div>
            </div>
            <span className="text-white/80 font-medium text-sm">ðŸ–¥ï¸ {title}</span>
          </div>

          <div className="flex gap-2">
            <button 
              onClick={handleRefresh}
              className="text-white/60 hover:text-white px-2 py-1 rounded text-sm hover:bg-white/10 transition-all"
              title="Refresh"
            >
              ðŸ”„
            </button>
            <button 
              onClick={() => setShowCode(!showCode)}
              className={`px-2 py-1 rounded text-sm transition-all ${showCode ? 'bg-white/20 text-white' : 'text-white/60 hover:text-white hover:bg-white/10'}`}
              title="View Code"
            >
              {showCode ? 'ðŸ“± Preview' : 'ðŸ’» Code'}
            </button>
            {onEdit && (
              <button 
                onClick={onEdit}
                className="text-white/60 hover:text-white px-2 py-1 rounded text-sm hover:bg-white/10 transition-all"
                title="Edit Code"
              >
                âœï¸ Edit
              </button>
            )}
            <button 
              onClick={handleDownload}
              className="text-white/60 hover:text-white px-2 py-1 rounded text-sm hover:bg-white/10 transition-all"
              title="Download"
            >
              â¬‡ï¸ Download
            </button>
            <button 
              onClick={() => setIsFullscreen(!isFullscreen)}
              className="text-white/60 hover:text-white px-2 py-1 rounded text-sm hover:bg-white/10 transition-all"
              title={isFullscreen ? "Exit Fullscreen" : "Fullscreen"}
            >
              {isFullscreen ? 'ðŸ—•' : 'â›¶'}
            </button>
          </div>
        </div>

        {/* Preview content - MUCH LARGER */}
        <div className={`${isFullscreen ? 'flex-1' : 'h-[600px]'} overflow-hidden`}>
          {showCode ? (
            <pre className="h-full overflow-auto p-4 text-sm text-green-400 bg-black/50 font-mono">
              {code}
            </pre>
          ) : (
            <iframe
              ref={iframeRef}
              key={key}
              title="App Preview"
              srcDoc={code}
              className="w-full h-full border-0 bg-white"
              sandbox="allow-scripts allow-forms allow-modals"
            />
          )}
        </div>
      </div>

      {isFullscreen && (
        <button
          onClick={() => setIsFullscreen(false)}
          className="absolute top-6 right-6 bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-full font-medium backdrop-blur-sm"
        >
          âœ• Close Fullscreen
        </button>
      )}
    </div>
  );
};

// ============================================
// CODE STREAMING ANIMATION
// ============================================

const CodeStreamingAnimation = ({ isActive, onComplete }) => {
  const [lines, setLines] = useState([]);
  const [currentLine, setCurrentLine] = useState(0);

  const codeSnippets = [
    '// Initializing your app...',
    'import React from "react";',
    '',
    'function App() {',
    '  const [state, setState] = useState({});',
    '  ',
    '  // Building your interface...',
    '  return (',
    '    <div className="app-container">',
    '      <header>',
    '        <h1>Your Amazing App</h1>',
    '      </header>',
    '      <main>',
    '        {/* Adding your features... */}',
    '        <Component1 />',
    '        <Component2 />',
    '      </main>',
    '    </div>',
    '  );',
    '}',
    '',
    'export default App;',
    '',
    '// âœ… Build complete!'
  ];

  useEffect(() => {
    if (!isActive) {
      setLines([]);
      setCurrentLine(0);
      return;
    }

    const interval = setInterval(() => {
      setCurrentLine(prev => {
        if (prev >= codeSnippets.length) {
          clearInterval(interval);
          onComplete?.();
          return prev;
        }
        setLines(l => [...l, codeSnippets[prev]]);
        return prev + 1;
      });
    }, 150);

    return () => clearInterval(interval);
  }, [isActive, onComplete]);

  return (
    <div className="bg-gray-900 rounded-xl p-4 font-mono text-sm overflow-hidden h-64">
      <div className="flex items-center gap-2 mb-3 text-green-400 text-xs">
        <span className="animate-pulse">â—</span>
        <span>BYTE is coding...</span>
      </div>
      <div className="space-y-0.5 overflow-y-auto h-48">
        {lines.map((line, i) => (
          <div 
            key={i} 
            className={`${
              line.startsWith('//') 
                ? 'text-gray-500' 
                : line.includes('function') || line.includes('const') || line.includes('import')
                  ? 'text-purple-400'
                  : line.includes('<') || line.includes('>')
                    ? 'text-blue-400'
                    : 'text-green-300'
            } ${i === lines.length - 1 ? 'animate-pulse' : ''}`}
          >
            {line || '\u00A0'}
          </div>
        ))}
        {isActive && currentLine < codeSnippets.length && (
          <div className="text-green-400 animate-pulse">â–Œ</div>
        )}
      </div>
    </div>
  );
};

// ============================================
// FLOATING PARTICLES
// ============================================

const FloatingParticles = ({ count = 20, color = 'rgba(72, 168, 154, 0.15)' }) => {
  const particles = Array.from({ length: count }, (_, i) => ({
    id: i,
    size: Math.random() * 8 + 4,
    x: Math.random() * 100,
    duration: Math.random() * 20 + 10,
    delay: Math.random() * 5,
  }));

  return (
    <div className="fixed inset-0 pointer-events-none overflow-hidden z-0">
      {particles.map(p => (
        <div
          key={p.id}
          className="absolute rounded-full"
          style={{
            width: p.size,
            height: p.size,
            left: `${p.x}%`,
            background: color,
            animation: `float-up ${p.duration}s linear ${p.delay}s infinite`,
          }}
        />
      ))}
      <style jsx>{`
        @keyframes float-up {
          0% { transform: translateY(100vh) rotate(0deg); opacity: 0; }
          10% { opacity: 0.6; }
          90% { opacity: 0.6; }
          100% { transform: translateY(-100vh) rotate(360deg); opacity: 0; }
        }
      `}</style>
    </div>
  );
};

// ============================================
// REVEAL CARD ANIMATION
// ============================================

const RevealCard = ({ children, delay = 0, className = '' }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setIsVisible(true), delay);
    return () => clearTimeout(timer);
  }, [delay]);

  return (
    <div className={`transform transition-all duration-700 ${
      isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
    } ${className}`}>
      {children}
    </div>
  );
};

// ============================================
// INPUT STEP WITH BYTE EXAMPLES
// ============================================

const InputStep = ({ label, placeholder, value, onChange, byteMessage, byteExamples, hint, multiline = false }) => (
  <div className="space-y-4">
    <ByteMentor message={byteMessage} mood="encouraging" />

    <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-6 border border-white/10">
      <label className="block text-xl font-bold text-white mb-4">{label}</label>

      {byteExamples && (
        <div className="mb-4 p-4 bg-white/5 rounded-xl border border-white/10">
          <div className="text-sm font-medium text-white/60 mb-3">ðŸ’¡ BYTE's Examples (click to use):</div>
          <div className="grid gap-2">
            {byteExamples.map((ex, i) => (
              <button 
                key={i}
                type="button"
                className="block w-full text-left text-white/80 text-sm cursor-pointer hover:text-[#48a89a] hover:bg-[#48a89a]/10 p-3 rounded-lg transition-all border border-transparent hover:border-[#48a89a]/30"
                onClick={() => onChange(ex)}
              >
                "{ex}"
              </button>
            ))}
          </div>
        </div>
      )}

      {multiline ? (
        <textarea 
          value={value}
          onChange={(e) => onChange(e.target.value)}
          placeholder={placeholder}
          className="w-full p-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-white/40 focus:ring-2 focus:ring-[#48a89a] focus:border-transparent outline-none min-h-[150px] resize-none"
        />
      ) : (
        <input 
          type="text"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          placeholder={placeholder}
          className="w-full p-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-white/40 focus:ring-2 focus:ring-[#48a89a] focus:border-transparent outline-none text-lg"
        />
      )}

      {hint && (
        <p className="text-white/50 text-sm mt-2 italic">{hint}</p>
      )}
    </div>
  </div>
);

// ============================================
// MAIN VIBE CODE CHALLENGE COMPONENT
// ============================================

const VibeCodeChallenge = ({ gameCode, room, userId, isHost, onBack, onOpenDashboard }) => {
  // Form state
  const [appIdea, setAppIdea] = useState('');
  const [appDescription, setAppDescription] = useState('');
  const [selectedFeatures, setSelectedFeatures] = useState([]);
  const [iterationFeedback, setIterationFeedback] = useState('');

  // Generated content
  const [generatedCode, setGeneratedCode] = useState('');
  const [mockupUrl, setMockupUrl] = useState('');

  // UI state
  const [isGenerating, setIsGenerating] = useState(false);
  const [showCodeStream, setShowCodeStream] = useState(false);
  const [defeatedVillains, setDefeatedVillains] = useState([]);
  const [iterationCount, setIterationCount] = useState(0);
  const [error, setError] = useState('');

  const phases = ['lobby', 'intro', 'ideate', 'describe', 'features', 'generate', 'iterate', 'polish', 'present', 'vote', 'results'];

  // Feature options
  const featureOptions = [
    { id: 'responsive', name: 'ðŸ“± Responsive Design', desc: 'Works on any screen size' },
    { id: 'darkmode', name: 'ðŸŒ™ Dark Mode', desc: 'Easy on the eyes' },
    { id: 'animations', name: 'âœ¨ Animations', desc: 'Smooth transitions' },
    { id: 'forms', name: 'ðŸ“ Forms/Input', desc: 'Collect user data' },
    { id: 'images', name: 'ðŸ–¼ï¸ Image Gallery', desc: 'Display photos' },
    { id: 'search', name: 'ðŸ” Search', desc: 'Find content fast' },
    { id: 'cards', name: 'ðŸƒ Card Layout', desc: 'Organize info beautifully' },
    { id: 'navigation', name: 'ðŸ§­ Navigation', desc: 'Easy to explore' },
  ];

  // Handle code generation
  const handleGenerate = async () => {
    if (!hasApiKey()) {
      setError('API key required. Please set it in settings.');
      return;
    }

    setIsGenerating(true);
    setShowCodeStream(true);
    setError('');

    try {
      const prompt = `Create a ${appIdea}. Description: ${appDescription}. Features: ${selectedFeatures.join(', ')}`;
      const code = await generateInitialCode(prompt);

      setTimeout(() => {
        setGeneratedCode(code);
        setShowCodeStream(false);
        setIsGenerating(false);
        setDefeatedVillains(['chaos', 'confusion']);

        // Submit to game
        submitToGame(gameCode, {
          type: 'app-generated',
          playerId: userId,
          appIdea,
          appDescription,
          features: selectedFeatures,
          timestamp: Date.now()
        });
      }, 3000);

    } catch (err) {
      setError('Generation failed: ' + err.message);
      setIsGenerating(false);
      setShowCodeStream(false);
    }
  };

  // Handle iteration
  const handleIterate = async () => {
    if (!iterationFeedback.trim()) return;

    setIsGenerating(true);
    setError('');

    try {
      const updatedCode = await iterateCode(generatedCode, iterationFeedback);
      setGeneratedCode(updatedCode);
      setIterationCount(c => c + 1);
      setIterationFeedback('');

      if (iterationCount >= 1) {
        setDefeatedVillains(prev => [...prev, 'bugs']);
      }

    } catch (err) {
      setError('Iteration failed: ' + err.message);
    }

    setIsGenerating(false);
  };

  // Handle polish
  const handlePolish = async () => {
    setIsGenerating(true);

    try {
      const polishedCode = await polishCode(generatedCode);
      setGeneratedCode(polishedCode);
      setDefeatedVillains(prev => [...prev, 'complexity', 'scope']);

      // Submit final version
      submitToGame(gameCode, {
        type: 'app-complete',
        playerId: userId,
        appIdea,
        code: polishedCode,
        timestamp: Date.now()
      });

    } catch (err) {
      setError('Polish failed: ' + err.message);
    }

    setIsGenerating(false);
  };

  // ============================================
