                  <button
                    onClick={() => submitVote(gameCode, userId, sub.playerId)}
                    className="w-full bg-white/10 hover:bg-white/20 backdrop-blur-sm rounded-2xl p-6 border-2 border-transparent hover:border-[#d4a84b] transition-all text-left"
                  >
                    <div className="flex items-start gap-4">
                      <div className="text-5xl">ðŸ’»</div>
                      <div className="flex-1">
                        <h3 className="text-xl font-bold text-white mb-1">{sub.appIdea}</h3>
                        <p className="text-white/60 text-sm mb-3">by {creator?.name || 'Unknown'}</p>
                        <span className="bg-white/20 text-white px-3 py-1 rounded-full text-sm">
                          Click to Vote
                        </span>
                      </div>
                    </div>
                  </button>
                </RevealCard>
              );
            })}
          </div>

          {isHost && (
            <RevealCard delay={600}>
              <div className="text-center">
                <Button 
                  onClick={() => updateGamePhase(gameCode, 'results')}
                  className="bg-gradient-to-r from-[#48a89a] to-[#3d8a7e] text-white px-12 py-4 text-xl font-bold rounded-xl"
                >
                  ðŸ† See Results â†’
                </Button>
              </div>
            </RevealCard>
          )}
        </div>
      </div>
    );
  };

  const renderResults = () => {
    const players = Object.values(room?.players || {}).sort((a, b) => (b.votes || 0) - (a.votes || 0));

    return (
      <div className="min-h-screen bg-gradient-to-br from-[#1a1a2e] via-[#16213e] to-[#0f0f23] p-6 overflow-y-auto">
        <FloatingParticles count={40} />

        <div className="max-w-3xl mx-auto relative z-10 py-8">
          <RevealCard>
            <div className="text-center mb-12">
              <div className="text-8xl mb-4">ðŸ†</div>
              <h1 className="text-5xl font-black text-white mb-4">
                CHALLENGE <span className="text-[#d4a84b]">COMPLETE!</span>
              </h1>
              <p className="text-xl text-white/70">All villains have been defeated!</p>
            </div>
          </RevealCard>

          <VillainBanner 
            villains={['chaos', 'complexity', 'confusion', 'bugs', 'scope']} 
            defeated={['chaos', 'complexity', 'confusion', 'bugs', 'scope']}
          />

          <RevealCard delay={300}>
            <ByteMentor 
              message="Amazing work, everyone! You've proven that with AI as your partner, you can build anything. Keep creating!"
              mood="proud"
              size="large"
            />
          </RevealCard>

          <div className="space-y-4 my-8">
            {players.slice(0, 5).map((player, i) => (
              <RevealCard key={player.id} delay={500 + i * 150}>
                <div className={`flex items-center justify-between p-6 rounded-2xl ${
                  i === 0 
                    ? 'bg-gradient-to-r from-[#d4a84b] via-[#e8c36a] to-[#d4a84b] text-white shadow-lg shadow-[#d4a84b]/30'
                    : i === 1
                      ? 'bg-gradient-to-r from-gray-300 via-gray-200 to-gray-300 text-gray-800'
                      : i === 2
                        ? 'bg-gradient-to-r from-orange-300 via-orange-200 to-orange-300 text-orange-900'
                        : 'bg-white/10 text-white'
                }`}>
                  <div className="flex items-center gap-4">
                    <span className="text-4xl">
                      {i === 0 ? 'ðŸ¥‡' : i === 1 ? 'ðŸ¥ˆ' : i === 2 ? 'ðŸ¥‰' : `${i + 1}.`}
                    </span>
                    <span className="text-2xl font-bold">{player.name}</span>
                  </div>
                  <div className="text-right">
                    <div className="text-3xl font-black">{player.votes || 0}</div>
                    <div className="text-sm opacity-70">votes</div>
                  </div>
                </div>
              </RevealCard>
            ))}
          </div>

          <RevealCard delay={1200}>
            <div className="text-center">
              <Button 
                onClick={onBack}
                className="bg-gradient-to-r from-[#6b8cce] to-[#5070b0] text-white px-12 py-4 text-xl font-bold rounded-xl"
              >
                ðŸ  Back to Home
              </Button>
            </div>
          </RevealCard>
        </div>
      </div>
    );
  };

  // ============================================
  // MAIN RENDER
  // ============================================

  const renderPhase = () => {
    switch (room?.phase) {
      case 'lobby': return renderLobby();
      case 'intro': return renderIntro();
      case 'ideate': return renderIdeate();
      case 'describe': return renderDescribe();
      case 'features': return renderFeatures();
      case 'generate': return renderGenerate();
      case 'iterate': return renderIterate();
      case 'polish': return renderIterate(); // Reuse iterate
      case 'present': return renderPresent();
      case 'vote': return renderVote();
      case 'results': return renderResults();
      default: return renderLobby();
    }
  };

  return (
    <div className="relative">
      {renderPhase()}

      {/* Back button */}
      <button 
        onClick={onBack}
        className="fixed top-4 left-4 z-50 bg-white/10 hover:bg-white/20 backdrop-blur-sm text-white px-4 py-2 rounded-full font-medium transition-all"
      >
        â† Exit Game
      </button>

      {/* Dashboard button for host */}
      {isHost && (
        <button 
          onClick={onOpenDashboard}
          className="fixed top-4 right-4 z-50 bg-white/10 hover:bg-white/20 backdrop-blur-sm text-white px-4 py-2 rounded-full font-medium transition-all"
        >
          ðŸ“Š Dashboard
        </button>
      )}

      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fadeIn {
          animation: fadeIn 0.5s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

export default VibeCodeChallenge;
