// Facilitator Dashboard
// Central control panel for managing games

import React, { useState } from 'react';
import { db } from './firebase';
import { doc, updateDoc, arrayRemove } from 'firebase/firestore';

const DashboardCard = ({ children, className = '', title, actions }) => (
  <div className={`bg-white rounded-xl border border-[#e2e0dc] shadow-sm ${className}`}>
    {(title || actions) && (
      <div className="flex justify-between items-center px-4 py-3 border-b border-[#e2e0dc]">
        {title && <h3 className="font-semibold text-[#3d5a4c]">{title}</h3>}
        {actions && <div className="flex gap-2">{actions}</div>}
      </div>
    )}
    <div className="p-4">{children}</div>
  </div>
);

const StatusBadge = ({ status }) => {
  const colors = {
    waiting: 'bg-yellow-100 text-yellow-800',
    active: 'bg-green-100 text-green-800',
    completed: 'bg-blue-100 text-blue-800',
    paused: 'bg-gray-100 text-gray-800',
  };
  return (
    <span className={`px-2 py-1 rounded-full text-xs font-medium ${colors[status] || colors.waiting}`}>
      {status}
    </span>
  );
};

const ControlButton = ({ onClick, disabled, variant = 'default', children, loading }) => {
  const variants = {
    default: 'bg-[#f5f3ef] hover:bg-[#e2e0dc] text-[#3d5a4c]',
    primary: 'bg-[#3d5a4c] hover:bg-[#4a6b5a] text-white',
    accent: 'bg-[#48a89a] hover:bg-[#5bc4b4] text-white',
    danger: 'bg-red-500 hover:bg-red-600 text-white',
    warning: 'bg-yellow-500 hover:bg-yellow-600 text-white',
  };
  return (
    <button
      onClick={onClick}
      disabled={disabled || loading}
      className={`${variants[variant]} px-4 py-2 rounded-lg font-medium transition-all disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2`}
    >
      {loading && <span className="animate-spin">‚è≥</span>}
      {children}
    </button>
  );
};

export const FacilitatorDashboard = ({ gameCode, room, userId, onClose }) => {
  const [activeTab, setActiveTab] = useState('overview');
  // eslint-disable-next-line no-unused-vars
  const [displayMode, setDisplayMode] = useState(null);

  const phases = {
    spotTheFake: ['lobby', 'intro', 'round1', 'round1-debrief', 'round2', 'round2-debrief', 'round3-create', 'round3-vote', 'ethics', 'legal', 'quiz', 'results'],
    memeMachine: ['lobby', 'issue', 'tutorial', 'caption', 'image', 'preview', 'critique', 'edit', 'finalize', 'viral', 'results'],
    vibeCode: ['lobby', 'intro', 'design', 'wireframe', 'generate', 'iterate', 'polish', 'demo', 'vote', 'results'],
  };

  const currentPhaseIndex = phases[room?.gameType]?.indexOf(room?.phase) || 0;

  const setPhase = async (phase) => {
    const roomRef = doc(db, 'gameRooms', gameCode);
    await updateDoc(roomRef, { phase });
  };

  const goToNextPhase = () => {
    const gamePhases = phases[room?.gameType] || [];
    if (currentPhaseIndex < gamePhases.length - 1) {
      setPhase(gamePhases[currentPhaseIndex + 1]);
    }
  };

  const goToPrevPhase = () => {
    const gamePhases = phases[room?.gameType] || [];
    if (currentPhaseIndex > 0) {
      setPhase(gamePhases[currentPhaseIndex - 1]);
    }
  };

  const kickPlayer = async (oderId) => {
    if (!window.confirm('Remove this player from the game?')) return;
    const roomRef = doc(db, 'gameRooms', gameCode);
    const playerToRemove = room.players.find(p => p.id === oderId);
    if (playerToRemove) {
      await updateDoc(roomRef, { players: arrayRemove(playerToRemove) });
    }
  };

  const addTime = async (seconds) => {
    const roomRef = doc(db, 'gameRooms', gameCode);
    await updateDoc(roomRef, { timerExtension: (room.timerExtension || 0) + seconds });
  };

  const togglePause = async () => {
    const roomRef = doc(db, 'gameRooms', gameCode);
    await updateDoc(roomRef, { isPaused: !room.isPaused });
  };

  const setDisplay = (mode) => {
    setDisplayMode(mode);
    const roomRef = doc(db, 'gameRooms', gameCode);
    updateDoc(roomRef, { displayMode: mode });
  };

  const endGame = async () => {
    if (!window.confirm('End the game? This cannot be undone.')) return;
    setPhase('results');
  };

  const renderOverview = () => (
    <div className="space-y-6">
      <DashboardCard title="Game Status">
        <div className="grid grid-cols-3 gap-4">
          <div className="text-center">
            <div className="text-3xl font-bold text-[#3d5a4c]">{room?.players?.length || 0}</div>
            <div className="text-sm text-[#6b7c74]">Players</div>
          </div>
          <div className="text-center">
            <div className="text-3xl font-bold text-[#48a89a]">{room?.submissions?.length || 0}</div>
            <div className="text-sm text-[#6b7c74]">Submissions</div>
          </div>
          <div className="text-center">
            <div className="text-3xl font-bold text-[#d4a84b]">{currentPhaseIndex + 1}/{phases[room?.gameType]?.length || 1}</div>
            <div className="text-sm text-[#6b7c74]">Phase</div>
          </div>
        </div>
      </DashboardCard>

      <DashboardCard title="Phase Control">
        <div className="flex items-center gap-4">
          <ControlButton onClick={goToPrevPhase} disabled={currentPhaseIndex === 0}>
            ‚Üê Previous
          </ControlButton>
          <div className="flex-1 text-center">
            <div className="font-semibold text-[#3d5a4c]">{room?.phase}</div>
            <div className="text-xs text-[#6b7c74]">Current Phase</div>
          </div>
          <ControlButton onClick={goToNextPhase} disabled={currentPhaseIndex === phases[room?.gameType]?.length - 1} variant="primary">
            Next ‚Üí
          </ControlButton>
        </div>

        <div className="mt-4 flex gap-1 overflow-x-auto pb-2">
          {phases[room?.gameType]?.map((phase, i) => (
            <button
              key={phase}
              onClick={() => setPhase(phase)}
              className={`px-3 py-1 rounded-full text-xs whitespace-nowrap transition-all
                ${i === currentPhaseIndex 
                  ? 'bg-[#3d5a4c] text-white' 
                  : i < currentPhaseIndex 
                    ? 'bg-[#48a89a] text-white' 
                    : 'bg-[#f5f3ef] text-[#6b7c74] hover:bg-[#e2e0dc]'}`}
            >
              {phase}
            </button>
          ))}
        </div>
      </DashboardCard>

      <DashboardCard title="Timer Controls">
        <div className="flex items-center gap-4">
          <ControlButton onClick={togglePause} variant={room?.isPaused ? 'accent' : 'warning'}>
            {room?.isPaused ? '‚ñ∂Ô∏è Resume' : '‚è∏Ô∏è Pause'}
          </ControlButton>
          <ControlButton onClick={() => addTime(60)}>+1 min</ControlButton>
          <ControlButton onClick={() => addTime(300)}>+5 min</ControlButton>
          <ControlButton onClick={() => addTime(-60)} variant="danger">-1 min</ControlButton>
        </div>
      </DashboardCard>

      <DashboardCard title="Quick Actions">
        <div className="flex flex-wrap gap-2">
          <ControlButton onClick={() => setDisplay('leaderboard')} variant="accent">
            üìä Show Leaderboard
          </ControlButton>
          <ControlButton onClick={() => setDisplay('voting')}>
            üó≥Ô∏è Show Voting
          </ControlButton>
          <ControlButton onClick={() => setDisplay(null)}>
            üñ•Ô∏è Clear Display
          </ControlButton>
          <ControlButton onClick={endGame} variant="danger">
            üèÅ End Game
          </ControlButton>
        </div>
      </DashboardCard>
    </div>
  );

  const renderPlayers = () => (
    <div className="space-y-4">
      <DashboardCard title={`Players (${room?.players?.length || 0})`}>
        <div className="space-y-2">
          {room?.players?.map((player) => (
            <div key={player.id} className="flex items-center justify-between p-3 bg-[#f5f3ef] rounded-lg">
              <div className="flex items-center gap-3">
                <div className={`w-3 h-3 rounded-full ${player.isHost ? 'bg-[#d4a84b]' : 'bg-[#48a89a]'}`} />
                <div>
                  <div className="font-medium text-[#3d5a4c]">
                    {player.name} {player.isHost && 'üëë'}
                  </div>
                  <div className="text-xs text-[#6b7c74]">Score: {player.score || 0} pts</div>
                </div>
              </div>
              {!player.isHost && (
                <button onClick={() => kickPlayer(player.id)} className="text-xs text-red-500 hover:text-red-700">
                  Remove
                </button>
              )}
            </div>
          ))}
        </div>
      </DashboardCard>

      <DashboardCard title="Leaderboard">
        <div className="space-y-2">
          {[...room?.players || []].sort((a, b) => (b.score || 0) - (a.score || 0)).map((player, i) => (
            <div key={player.id} className={`flex items-center justify-between p-3 rounded-lg ${i === 0 ? 'bg-[#48a89a]/20' : 'bg-[#f5f3ef]'}`}>
              <div className="flex items-center gap-3">
                <div className="font-bold text-[#3d5a4c] w-6">{i + 1}.</div>
                <div className="font-medium text-[#3d5a4c]">{player.name}</div>
              </div>
              <div className="font-bold text-[#48a89a]">{player.score || 0} pts</div>
            </div>
          ))}
        </div>
      </DashboardCard>
    </div>
  );

  const tabs = [
    { id: 'overview', label: 'üìã Overview', render: renderOverview },
    { id: 'players', label: 'üë• Players', render: renderPlayers },
  ];

  return (
    <div className="fixed inset-0 bg-[#f5f3ef] z-50 overflow-hidden">
      <div className="bg-white border-b border-[#e2e0dc] px-4 py-3 flex items-center justify-between">
        <div className="flex items-center gap-4">
          <button onClick={onClose} className="text-[#6b7c74] hover:text-[#3d5a4c]">
            ‚Üê Back to Game
          </button>
          <div>
            <div className="font-bold text-[#3d5a4c]">Facilitator Dashboard</div>
            <div className="text-sm text-[#6b7c74]">
              Game: <span className="font-mono">{gameCode}</span> ‚Ä¢ {room?.gameType} ‚Ä¢ <StatusBadge status={room?.isPaused ? 'paused' : 'active'} />
            </div>
          </div>
        </div>
      </div>

      <div className="bg-white border-b border-[#e2e0dc] px-4">
        <div className="flex gap-1">
          {tabs.map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`px-4 py-3 text-sm font-medium transition-all border-b-2 
                ${activeTab === tab.id 
                  ? 'border-[#48a89a] text-[#3d5a4c]' 
                  : 'border-transparent text-[#6b7c74] hover:text-[#3d5a4c]'}`}
            >
              {tab.label}
            </button>
          ))}
        </div>
      </div>

      <div className="p-4 overflow-y-auto" style={{ height: 'calc(100vh - 130px)' }}>
        <div className="max-w-6xl mx-auto">
          {tabs.find(t => t.id === activeTab)?.render()}
        </div>
      </div>
    </div>
  );
};

export const ProjectorDisplay = ({ room, displayMode }) => {
  if (!displayMode) {
    return (
      <div className="fixed inset-0 bg-[#3d5a4c] flex items-center justify-center">
        <div className="text-center text-white">
          <div className="text-6xl mb-4">üéÆ</div>
          <div className="text-3xl font-bold">ARTIFICIAL</div>
          <div className="text-xl opacity-75">Waiting for facilitator...</div>
        </div>
      </div>
    );
  }

  if (displayMode === 'leaderboard') {
    const sortedPlayers = [...(room?.players || [])].sort((a, b) => (b.score || 0) - (a.score || 0));
    return (
      <div className="fixed inset-0 bg-gradient-to-br from-[#3d5a4c] to-[#2d4a3c] p-8">
        <div className="max-w-2xl mx-auto">
          <h1 className="text-4xl font-bold text-white text-center mb-8">üèÜ Leaderboard</h1>
          <div className="space-y-4">
            {sortedPlayers.map((player, i) => (
              <div 
                key={player.id}
                className={`flex items-center justify-between p-6 rounded-xl ${
                  i === 0 ? 'bg-[#d4a84b] text-white' :
                  i === 1 ? 'bg-gray-300 text-gray-800' :
                  i === 2 ? 'bg-amber-600 text-white' :
                  'bg-white/10 text-white'
                }`}
              >
                <div className="flex items-center gap-4">
                  <div className="text-3xl font-bold">{i + 1}</div>
                  <div className="text-2xl font-medium">{player.name}</div>
                </div>
                <div className="text-3xl font-bold">{player.score || 0}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return null;
};

export default FacilitatorDashboard;
