// FACILITATOR DASHBOARD v2 - Full Screen Projector View
// Advanced unified interface with proper named exports
import React, { useState, useEffect, useRef } from 'react';
import { Card, Button, Badge } from './components';
import { updateGamePhase, subscribeToRoom } from './firebase';

// ============================================
// ANIMATED COMPONENTS
// ============================================

// Particle System for Background
const ParticleField = ({ count = 50, color = 'rgba(72, 168, 154, 0.15)' }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext('2d');
    let animationId;
    let particles = [];

    const resize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    resize();
    window.addEventListener('resize', resize);

    // Initialize particles
    for (let i = 0; i < count; i++) {
      particles.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        vx: (Math.random() - 0.5) * 0.5,
        vy: (Math.random() - 0.5) * 0.5,
        size: Math.random() * 4 + 1,
        opacity: Math.random() * 0.5 + 0.2
      });
    }

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach(p => {
        p.x += p.vx;
        p.y += p.vy;

        if (p.x < 0 || p.x > canvas.width) p.vx *= -1;
        if (p.y < 0 || p.y > canvas.height) p.vy *= -1;

        ctx.beginPath();
        ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2);
        ctx.fillStyle = color.replace('0.15', p.opacity);
        ctx.fill();
      });

      // Draw connections
      particles.forEach((p1, i) => {
        particles.slice(i + 1).forEach(p2 => {
          const dx = p1.x - p2.x;
          const dy = p1.y - p2.y;
          const dist = Math.sqrt(dx * dx + dy * dy);

          if (dist < 100) {
            ctx.beginPath();
            ctx.moveTo(p1.x, p1.y);
            ctx.lineTo(p2.x, p2.y);
            ctx.strokeStyle = color.replace('0.15', (1 - dist / 100) * 0.1);
            ctx.stroke();
          }
        });
      });

      animationId = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      cancelAnimationFrame(animationId);
      window.removeEventListener('resize', resize);
    };
  }, [count, color]);

  return (
    <canvas 
      ref={canvasRef} 
      className="fixed inset-0 pointer-events-none z-0"
      style={{ opacity: 0.5 }}
    />
  );
};

// Animated Timer with Glow Effect
const BigTimer = ({ seconds, totalSeconds, phase, onTimeUp }) => {
  const isUrgent = seconds < 30;
  const isCritical = seconds < 10;
  const minutes = Math.floor(seconds / 60);
  const secs = seconds % 60;
  const progress = totalSeconds > 0 ? ((totalSeconds - seconds) / totalSeconds) * 100 : 0;

  const timerRef = useRef(null);

  useEffect(() => {
    if (isCritical && timerRef.current) {
      timerRef.current.animate([
        { transform: 'scale(1)' },
        { transform: 'scale(1.05)' },
        { transform: 'scale(1)' }
      ], {
        duration: 500,
        iterations: 1
      });
    }
  }, [seconds, isCritical]);

  return (
    <div 
      ref={timerRef}
      className={`rounded-3xl p-8 relative overflow-hidden transition-all duration-500 ${
        isCritical 
          ? 'bg-gradient-to-br from-red-600 to-red-800 shadow-[0_0_60px_rgba(220,38,38,0.5)]' 
          : isUrgent 
            ? 'bg-gradient-to-br from-amber-500 to-orange-600 shadow-[0_0_40px_rgba(245,158,11,0.4)]' 
            : 'bg-gradient-to-br from-[#3d5a4c] to-[#48a89a] shadow-[0_0_30px_rgba(72,168,154,0.3)]'
      }`}
    >
      {/* Animated rings */}
      <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
        <div className={`w-64 h-64 rounded-full border-4 opacity-20 ${isCritical ? 'animate-ping' : 'animate-pulse'} border-white`} />
        <div className="absolute w-48 h-48 rounded-full border-2 opacity-10 animate-spin border-white" style={{ animationDuration: '10s' }} />
      </div>

      <div className="text-white text-center relative z-10">
        <div className="text-sm uppercase tracking-[0.3em] mb-2 opacity-80 font-medium flex items-center justify-center gap-2">
          <span className="inline-block animate-pulse">â±ï¸</span>
          TIME REMAINING
        </div>

        <div className="text-8xl font-mono font-black tracking-tight" style={{ textShadow: '0 4px 20px rgba(0,0,0,0.3)' }}>
          {minutes}:{secs.toString().padStart(2, '0')}
        </div>

        {isCritical && (
          <div className="text-2xl font-bold mt-3 flex items-center justify-center gap-2">
            <span className="animate-bounce">âš ï¸</span>
            <span className="animate-pulse">FINAL COUNTDOWN!</span>
            <span className="animate-bounce" style={{ animationDelay: '0.1s' }}>âš ï¸</span>
          </div>
        )}

        {/* Progress bar */}
        <div className="mt-6 h-4 bg-white/20 rounded-full overflow-hidden backdrop-blur-sm">
          <div 
            className={`h-full transition-all duration-1000 rounded-full ${
              isCritical 
                ? 'bg-gradient-to-r from-white to-red-200' 
                : isUrgent 
                  ? 'bg-gradient-to-r from-yellow-200 to-white' 
                  : 'bg-gradient-to-r from-[#d4a84b] to-yellow-300'
            }`}
            style={{ width: `${progress}%` }}
          />
        </div>

        <div className="mt-4 px-4 py-2 bg-white/10 rounded-xl inline-block backdrop-blur-sm">
          <span className="text-sm font-medium opacity-90">
            ðŸ“ {phase?.toUpperCase().replace(/-/g, ' ') || 'WAITING'}
          </span>
        </div>
      </div>
    </div>
  );
};

// Stats Card with Animation
const StatsCard = ({ icon, label, value, color = 'bg-[#3d5a4c]', trend, animate = true }) => {
  const [displayValue, setDisplayValue] = useState(0);

  useEffect(() => {
    if (!animate) {
      setDisplayValue(value);
      return;
    }

    const target = parseInt(value) || 0;
    const duration = 1000;
    const steps = 30;
    const increment = target / steps;
    let current = 0;

    const timer = setInterval(() => {
      current += increment;
      if (current >= target) {
        setDisplayValue(target);
        clearInterval(timer);
      } else {
        setDisplayValue(Math.floor(current));
      }
    }, duration / steps);

    return () => clearInterval(timer);
  }, [value, animate]);

  return (
    <div className={`${color} rounded-2xl p-5 text-white text-center relative overflow-hidden group hover:scale-105 transition-transform duration-300`}>
      {/* Shine effect */}
      <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/10 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-1000" />

      <div className="text-4xl mb-2 filter drop-shadow-lg">{icon}</div>
      <div className="text-5xl font-black" style={{ textShadow: '0 2px 10px rgba(0,0,0,0.2)' }}>
        {displayValue}
        {trend && (
          <span className={`text-lg ml-1 ${trend > 0 ? 'text-green-300' : 'text-red-300'}`}>
            {trend > 0 ? 'â†‘' : 'â†“'}{Math.abs(trend)}
          </span>
        )}
      </div>
      <div className="text-sm opacity-80 font-medium mt-1">{label}</div>
    </div>
  );
};

// Activity Feed with Animations
const ActivityFeed = ({ activities = [] }) => {
  const feedRef = useRef(null);

  useEffect(() => {
    if (feedRef.current && activities.length > 0) {
      feedRef.current.scrollTop = 0;
    }
  }, [activities]);

  return (
    <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-5 h-80 overflow-hidden shadow-lg">
      <h3 className="text-xl font-black text-[#3d5a4c] mb-4 flex items-center gap-2">
        <span className="animate-pulse">ðŸ“‹</span>
        LIVE ACTIVITY
      </h3>

      <div ref={feedRef} className="space-y-3 h-56 overflow-y-auto pr-2 custom-scrollbar">
        {activities.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full text-gray-400">
            <div className="text-5xl mb-3 animate-bounce">ðŸ‘€</div>
            <p>Waiting for activity...</p>
          </div>
        ) : (
          activities.slice(-15).reverse().map((activity, i) => (
            <div 
              key={i} 
              className={`p-3 rounded-xl text-sm transition-all duration-500 ${
                i === 0 
                  ? 'bg-gradient-to-r from-[#d4a84b]/30 to-[#48a89a]/20 border-2 border-[#d4a84b] animate-slideIn' 
                  : 'bg-[#f5f3ef] hover:bg-[#e8e6e2]'
              }`}
              style={{ animationDelay: `${i * 50}ms` }}
            >
              <div className="flex items-center gap-2">
                <span className="text-lg">{activity.icon}</span>
                <span className="font-bold text-[#3d5a4c]">{activity.player}</span>
                <span className="text-gray-500">{activity.action}</span>
              </div>
              <span className="text-xs text-gray-400 ml-7">{activity.time}</span>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Leaderboard with Podium Effect
const Leaderboard = ({ players = [] }) => {
  const sortedPlayers = [...players].sort((a, b) => (b.score || 0) - (a.score || 0));

  return (
    <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-5 shadow-lg">
      <h3 className="text-xl font-black text-[#3d5a4c] mb-4 flex items-center gap-2">
        <span>ðŸ†</span>
        LEADERBOARD
      </h3>

      {sortedPlayers.length === 0 ? (
        <div className="flex flex-col items-center justify-center h-32 text-gray-400">
          <div className="text-4xl mb-2">ðŸŽ®</div>
          <p>No scores yet</p>
        </div>
      ) : (
        <div className="space-y-2">
          {sortedPlayers.slice(0, 10).map((player, i) => (
            <div 
              key={player.id}
              className={`flex justify-between items-center p-3 rounded-xl transition-all duration-300 hover:scale-[1.02] ${
                i === 0 
                  ? 'bg-gradient-to-r from-[#d4a84b] via-[#e8c36a] to-[#d4a84b] text-white shadow-lg shadow-[#d4a84b]/30' 
                  : i === 1 
                    ? 'bg-gradient-to-r from-gray-300 via-gray-200 to-gray-300 text-gray-800 shadow-md' 
                    : i === 2 
                      ? 'bg-gradient-to-r from-orange-300 via-orange-200 to-orange-300 text-orange-900 shadow-md' 
                      : 'bg-[#f5f3ef] hover:bg-[#e8e6e2]'
              }`}
            >
              <div className="flex items-center gap-3">
                <span className="text-2xl">
                  {i === 0 ? 'ðŸ¥‡' : i === 1 ? 'ðŸ¥ˆ' : i === 2 ? 'ðŸ¥‰' : `${i + 1}.`}
                </span>
                <span className="font-bold">{player.name}</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="font-black text-2xl">{player.score || 0}</span>
                <span className="text-sm opacity-70">pts</span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Player Grid with Status Indicators
const PlayerGrid = ({ players = [] }) => (
  <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-5 shadow-lg">
    <h3 className="text-xl font-black text-[#3d5a4c] mb-4 flex items-center gap-2">
      <span>ðŸ‘¥</span>
      PLAYERS ({players.length})
    </h3>

    <div className="grid grid-cols-4 gap-3 max-h-48 overflow-y-auto">
      {players.map((player, i) => (
        <div 
          key={player.id}
          className={`p-3 rounded-xl text-center transition-all duration-300 ${
            player.submitted 
              ? 'bg-gradient-to-br from-[#48a89a] to-[#3d8a7e] text-white shadow-md' 
              : player.online === false 
                ? 'bg-gray-200 text-gray-400 opacity-50' 
                : 'bg-[#f5f3ef] text-[#3d5a4c]'
          }`}
        >
          <div className="text-2xl mb-1">
            {player.submitted ? 'âœ…' : player.online === false ? 'ðŸ’¤' : 'ðŸŽ®'}
          </div>
          <div className="text-sm font-bold truncate">{player.name}</div>
        </div>
      ))}
    </div>
  </div>
);

// Phase Controls with Progress
const PhaseControls = ({ room, gameCode, phases }) => {
  const currentIndex = phases.indexOf(room?.phase);
  const progress = phases.length > 0 ? ((currentIndex + 1) / phases.length) * 100 : 0;

  return (
    <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-5 shadow-lg">
      <h3 className="text-xl font-black text-[#3d5a4c] mb-4 flex items-center gap-2">
        <span>ðŸŽ¯</span>
        GAME FLOW
      </h3>

      {/* Progress bar */}
      <div className="h-3 bg-gray-200 rounded-full overflow-hidden mb-4">
        <div 
          className="h-full bg-gradient-to-r from-[#48a89a] to-[#d4a84b] transition-all duration-500"
          style={{ width: `${progress}%` }}
        />
      </div>

      {/* Phase indicator */}
      <div className="flex items-center justify-between mb-4">
        <span className="text-sm text-gray-500">Phase {currentIndex + 1} of {phases.length}</span>
        <span className="font-bold text-[#3d5a4c] uppercase">{room?.phase?.replace(/-/g, ' ')}</span>
      </div>

      {/* Navigation */}
      <div className="flex gap-2">
        <Button
          onClick={() => {
            const prevPhase = phases[Math.max(0, currentIndex - 1)];
            if (prevPhase) updateGamePhase(gameCode, prevPhase);
          }}
          disabled={currentIndex <= 0}
          className="flex-1 bg-gray-300 hover:bg-gray-400 disabled:opacity-30"
        >
          â¬…ï¸ Back
        </Button>
        <Button
          onClick={() => {
            const nextPhase = phases[Math.min(phases.length - 1, currentIndex + 1)];
            if (nextPhase) updateGamePhase(gameCode, nextPhase);
          }}
          disabled={currentIndex >= phases.length - 1}
          className="flex-1 bg-[#48a89a] hover:bg-[#3d8a7e]"
        >
          Next âž¡ï¸
        </Button>
      </div>
    </div>
  );
};

// Submission Gallery with Reactions
const SubmissionGallery = ({ submissions = [] }) => (
  <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-5 shadow-lg">
    <h3 className="text-xl font-black text-[#3d5a4c] mb-4 flex items-center gap-2">
      <span>ðŸ–¼ï¸</span>
      SUBMISSIONS ({submissions.length})
    </h3>

    {submissions.length === 0 ? (
      <div className="flex flex-col items-center justify-center h-32 text-gray-400">
        <div className="text-4xl mb-2">ðŸ“</div>
        <p>No submissions yet</p>
      </div>
    ) : (
      <div className="grid grid-cols-3 gap-3 max-h-64 overflow-y-auto">
        {submissions.map((sub, i) => (
          <div 
            key={i}
            className="bg-[#f5f3ef] rounded-xl p-3 hover:shadow-md transition-all duration-300 cursor-pointer hover:scale-105"
          >
            {sub.imageUrl && (
              <img 
                src={sub.imageUrl} 
                alt="Submission" 
                className="w-full h-24 object-cover rounded-lg mb-2"
              />
            )}
            <div className="text-sm font-bold truncate text-[#3d5a4c]">{sub.playerName}</div>
            {sub.caption && (
              <div className="text-xs text-gray-500 truncate mt-1">{sub.caption}</div>
            )}
            {sub.reactions && (
              <div className="flex gap-1 mt-2 text-xs">
                {Object.entries(sub.reactions).map(([emoji, count]) => (
                  <span key={emoji} className="bg-white rounded-full px-2 py-0.5">
                    {emoji} {count}
                  </span>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    )}
  </div>
);

// Quick Actions Panel
