export const FacilitatorDashboard = ({ gameCode, room, userId, onClose }) => {
  const [timerSeconds, setTimerSeconds] = useState(180);
  const [isPaused, setIsPaused] = useState(false);
  const [activities, setActivities] = useState([]);

  const phases = {
    spotTheFake: ['lobby', 'intro', 'round1', 'round1-debrief', 'round2', 'round2-debrief', 'ethics', 'legal', 'quiz', 'werewolf', 'werewolf-vote', 'results'],
    memeMachine: ['lobby', 'intro', 'issue', 'describe', 'wizard', 'generate', 'caption', 'preview', 'critique', 'finalize', 'gallery', 'vote', 'results'],
    vibeCode: ['lobby', 'intro', 'ideate', 'describe', 'generate', 'iterate', 'present', 'vote', 'results'],
  };

  const currentPhases = phases[room?.gameType] || [];

  // Timer countdown
  useEffect(() => {
    if (isPaused || timerSeconds <= 0) return;

    const timer = setInterval(() => {
      setTimerSeconds(t => Math.max(0, t - 1));
    }, 1000);

    return () => clearInterval(timer);
  }, [isPaused, timerSeconds]);

  // Track player activities
  useEffect(() => {
    if (!room?.players) return;

    const players = Object.values(room.players || {});
    const submissions = Object.values(room.submissions || {});

    // Generate activity feed from room state
    const newActivities = [];

    players.forEach(p => {
      newActivities.push({
        icon: 'ðŸ‘‹',
        player: p.name,
        action: 'joined the game',
        time: 'earlier'
      });
    });

    submissions.forEach(s => {
      newActivities.push({
        icon: 'âœ…',
        player: s.playerName || 'Someone',
        action: 'submitted their work',
        time: 'recently'
      });
    });

    setActivities(newActivities);
  }, [room]);

  const players = Object.values(room?.players || {});
  const submissions = Object.values(room?.submissions || {});
  const submittedCount = players.filter(p => p.submitted).length;

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#1a1a2e] via-[#16213e] to-[#0f0f23] relative overflow-hidden">
      <ParticleField count={40} />

      {/* Header */}
      <div className="relative z-10 px-6 py-4 border-b border-white/10 bg-black/20 backdrop-blur-sm">
        <div className="flex justify-between items-center max-w-[1800px] mx-auto">
          <div className="flex items-center gap-4">
            <h1 className="text-3xl font-black text-white tracking-tight">
              ðŸŽ® FACILITATOR DASHBOARD
            </h1>
            <Badge className="bg-gradient-to-r from-[#48a89a] to-[#d4a84b] text-white text-lg px-4 py-1">
              {gameCode}
            </Badge>
          </div>

          <div className="flex items-center gap-4">
            <span className="text-white/70 text-sm">
              {room?.gameType?.toUpperCase()} â€¢ {players.length} Players
            </span>
            <Button 
              onClick={onClose}
              className="bg-white/10 hover:bg-white/20 text-white"
            >
              âœ• Close Dashboard
            </Button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 p-6 max-w-[1800px] mx-auto">
        <div className="grid grid-cols-12 gap-6">

          {/* Left Column - Timer & Stats */}
          <div className="col-span-4 space-y-6">
            <BigTimer 
              seconds={timerSeconds}
              totalSeconds={180}
              phase={room?.phase}
            />

            <div className="grid grid-cols-2 gap-4">
              <StatsCard 
                icon="ðŸ‘¥" 
                label="Players" 
                value={players.length}
                color="bg-gradient-to-br from-[#6b8cce] to-[#4a6eb0]"
              />
              <StatsCard 
                icon="âœ…" 
                label="Submitted" 
                value={submittedCount}
                color="bg-gradient-to-br from-[#48a89a] to-[#3d8a7e]"
              />
              <StatsCard 
                icon="ðŸŽ¯" 
                label="Round" 
                value={Math.max(1, currentPhases.indexOf(room?.phase) + 1)}
                color="bg-gradient-to-br from-[#d4a84b] to-[#c49a3a]"
              />
              <StatsCard 
                icon="ðŸ“Š" 
                label="Total" 
                value={submissions.length}
                color="bg-gradient-to-br from-purple-500 to-purple-700"
              />
            </div>

            <QuickActions 
              gameCode={gameCode}
              room={room}
              onAddTime={() => setTimerSeconds(t => t + 60)}
              onPause={() => setIsPaused(!isPaused)}
              onRemind={() => alert('Reminder sent to all players!')}
              onSkip={() => {
                const currentIndex = currentPhases.indexOf(room?.phase);
                const nextPhase = currentPhases[Math.min(currentPhases.length - 1, currentIndex + 1)];
                if (nextPhase) updateGamePhase(gameCode, nextPhase);
              }}
              onEnd={() => updateGamePhase(gameCode, 'results')}
            />
          </div>

          {/* Middle Column - Activity & Submissions */}
          <div className="col-span-5 space-y-6">
            <ActivityFeed activities={activities} />
            <SubmissionGallery submissions={submissions} />
          </div>

          {/* Right Column - Leaderboard & Controls */}
          <div className="col-span-3 space-y-6">
            <Leaderboard players={players} />
            <PlayerGrid players={players} />
            <PhaseControls 
              room={room}
              gameCode={gameCode}
              phases={currentPhases}
            />
          </div>
        </div>

        {/* Pro Tip Footer */}
        <div className="mt-6 text-center py-4 bg-white/5 rounded-2xl backdrop-blur-sm">
          <p className="text-white/50 text-sm">
            ðŸ’¡ <strong>Pro Tip:</strong> Press F11 for fullscreen mode. Connect to a TV or projector for the best experience!
          </p>
        </div>
      </div>

      {/* Custom scrollbar styles */}
      <style jsx>{`
        .custom-scrollbar::-webkit-scrollbar {
          width: 6px;
        }
        .custom-scrollbar::-webkit-scrollbar-track {
          background: transparent;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: #48a89a;
          border-radius: 3px;
        }
        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateX(-20px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
        .animate-slideIn {
          animation: slideIn 0.3s ease-out;
        }
      `}</style>
    </div>
  );
};

// ============================================
// PROJECTOR DISPLAY COMPONENT
// ============================================

export const ProjectorDisplay = ({ room, displayMode = 'waiting' }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const players = Object.values(room?.players || {});
  const submissions = Object.values(room?.submissions || {});

  // Auto-rotate through content
  useEffect(() => {
    if (displayMode === 'gallery' && submissions.length > 0) {
      const timer = setInterval(() => {
        setCurrentSlide(s => (s + 1) % submissions.length);
      }, 5000);
      return () => clearInterval(timer);
    }
  }, [displayMode, submissions.length]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#1a1a2e] via-[#16213e] to-[#0f0f23] flex items-center justify-center relative overflow-hidden">
      <ParticleField count={30} />

      <div className="relative z-10 text-center max-w-4xl mx-auto px-8">
        {displayMode === 'waiting' && (
          <div className="animate-fadeIn">
            <div className="text-9xl mb-8 animate-bounce">ðŸŽ®</div>
            <h1 className="text-6xl font-black text-white mb-4 tracking-tight">
              WAITING FOR PLAYERS
            </h1>
            <div className="text-8xl font-mono font-black text-[#48a89a] mb-8 tracking-widest">
              {room?.code || '------'}
            </div>
            <p className="text-2xl text-white/70">
              Join at <span className="text-[#d4a84b] font-bold">artificial.game</span>
            </p>

            <div className="mt-12 flex justify-center gap-4 flex-wrap">
              {players.map((player, i) => (
                <div 
                  key={player.id}
                  className="bg-white/10 backdrop-blur-sm px-6 py-3 rounded-full text-white font-medium animate-fadeIn"
                  style={{ animationDelay: `${i * 100}ms` }}
                >
                  {player.name}
                </div>
              ))}
            </div>
          </div>
        )}

        {displayMode === 'gallery' && submissions.length > 0 && (
          <div className="animate-fadeIn">
            <h2 className="text-4xl font-black text-white mb-8">ðŸ–¼ï¸ SUBMISSION GALLERY</h2>

            <div className="bg-white/10 backdrop-blur-sm rounded-3xl p-8">
              {submissions[currentSlide]?.imageUrl && (
                <img 
                  src={submissions[currentSlide].imageUrl}
                  alt="Submission"
                  className="max-h-[50vh] mx-auto rounded-2xl shadow-2xl mb-6"
                />
              )}

              <div className="text-3xl font-bold text-white mb-2">
                {submissions[currentSlide]?.playerName}
              </div>

              {submissions[currentSlide]?.caption && (
                <p className="text-xl text-white/80 italic">
                  "{submissions[currentSlide].caption}"
                </p>
              )}
            </div>

            {/* Slide indicators */}
            <div className="flex justify-center gap-2 mt-6">
              {submissions.map((_, i) => (
                <button
                  key={i}
                  onClick={() => setCurrentSlide(i)}
                  className={`w-3 h-3 rounded-full transition-all ${
                    i === currentSlide 
                      ? 'bg-[#48a89a] w-8' 
                      : 'bg-white/30 hover:bg-white/50'
                  }`}
                />
              ))}
            </div>
          </div>
        )}

        {displayMode === 'results' && (
          <div className="animate-fadeIn">
            <h2 className="text-5xl font-black text-white mb-8">ðŸ† FINAL RESULTS</h2>

            <div className="space-y-4">
              {players
                .sort((a, b) => (b.score || 0) - (a.score || 0))
                .slice(0, 5)
                .map((player, i) => (
                  <div 
                    key={player.id}
                    className={`flex items-center justify-between p-6 rounded-2xl ${
                      i === 0 
                        ? 'bg-gradient-to-r from-[#d4a84b] to-[#e8c36a] text-white text-3xl' 
                        : i === 1 
                          ? 'bg-gradient-to-r from-gray-400 to-gray-300 text-gray-800 text-2xl' 
                          : i === 2 
                            ? 'bg-gradient-to-r from-orange-400 to-orange-300 text-orange-900 text-2xl' 
                            : 'bg-white/10 text-white text-xl'
                    }`}
                    style={{ animationDelay: `${i * 200}ms` }}
                  >
                    <div className="flex items-center gap-4">
                      <span className="text-4xl">
                        {i === 0 ? 'ðŸ¥‡' : i === 1 ? 'ðŸ¥ˆ' : i === 2 ? 'ðŸ¥‰' : `${i + 1}.`}
                      </span>
                      <span className="font-bold">{player.name}</span>
                    </div>
                    <span className="font-black">{player.score || 0} pts</span>
                  </div>
                ))
              }
            </div>
          </div>
        )}
      </div>

      {/* Game code always visible */}
      <div className="absolute bottom-6 right-6 bg-black/50 backdrop-blur-sm px-6 py-3 rounded-full">
        <span className="text-white/70 text-lg">Code: </span>
        <span className="text-[#48a89a] font-mono font-bold text-2xl">{room?.code}</span>
      </div>

      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fadeIn {
          animation: fadeIn 0.5s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

